function ds_sill_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var str = '';
	obj.fireevent = false;
	switch( strColumnID ) 
	{
		/*
		case "PLANG":
			if( gfn_isDupGridValue(grd_lang, true, nRow, strColumnID) ) 
			{
				break;
			}	
		*/


		case "CODE1":
			ds_sill.SetColumn(nRow,"CODE2","");
			ds_sill.SetColumn(nRow,"CODE3","");
			//fn_changeZztemno_grd_cd(ds_list4.GetColumn(nRow,strColumnID));
			//ds_code2.filter("HIG_CODE == " + quote( varNewValue) );
			break;

		case "CODE2":
			ds_sill.SetColumn(nRow,"CODE3","");
			//fn_changeZztemno_grd_cd(ds_list4.GetColumn(nRow,strColumnID));
			//ds_code3.filter("HIG_CODE == " + quote( varNewValue) );
			break;	

		case "CODE3":
			/*
			if ( length(varNewValue) == 0 || ds_sill.rowCount() < 2 ) 
			{
				return false;
			}
			str = ds_sill.getColumn(nRow, "CODE1")
		    gfn_showMsg("CW00018", lbTxt);
			dsObj.setColumn(row, dsColNm, "");
			*/
			if( ufn_isDupGridValue(div_pop.grd_list2, true, nRow, "CODE1", "CDOE2", strColumnID) ) 
			{
				break;
			}	
			break;

		default:
			//ALERT("AA");
			gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
	}

	obj.fireevent = true;

}


/********************************************************************************
* 기      능   :  그리드의 입력된 데이터 중복 여부 확인하기
********************************************************************************/
function ufn_isDupGridValue(gridObj, isShowMsg, row, dsColNm1, dsColNm2, dsColNm3, lbTxt) {
    var dsObj = object(gridObj.bindDataset);
    var str = dsObj.getColumn(row, dsColNm1) + dsObj.getColumn(row, dsColNm2) + dsObj.getColumn(row, dsColNm3) ;

    if ( length(str) == 0 || dsObj.rowCount() < 2 ) 
    {
        return false;
    }

    for( var i = 0; i < dsObj.rowCount(); i++) 
    {
        if ( i == row ) 
        {
            continue;
        }

        // 같은 경우
        if ( (dsObj.getColumn(i, dsColNm1) + dsObj.getColumn(i, dsColNm2) + dsObj.getColumn(i, dsColNm3) ) == str) 
        {
			if ( isShowMsg ) 
			{
				if ( length(lbTxt) == 0 ) 
				{
					 var r= gfn_getColIdx( gridObj, dsColNm3 );
					lbTxt = '검사Part, 검사항목, 검사지적사항' ; //, trim(gridObj.getCellProp("head", r, "text"));
				}

				//  항목의 값은 중복하여 입력할 수 없습니다.\n다른 값을 입력하여 주십시오."
				gfn_showMsg("CW00018", lbTxt);
				dsObj.setColumn(row, dsColNm3, "");
			}
            return true;
        }
    }

    return false;
}


