function fn_query() {


	//gfn_clearMsgBar();

	var tmp_txt	= ds_gbn.GetColumn(ds_gbn.currow, "CODE_NAME");

	if ( !gfn_checkFromToObj(cal_from,   cal_to,   true, tmp_txt,         0, true)   ) 
	{	return; 	}



	//if(ufn_isNull(cal_from, true, "시작일", cal_from)) return false;			//최소입력조건:시작일
	//if(ufn_isNull(cal_to, true, "종료일", cal_to)) return false;				//최소입력조건:종료일

	var user_grp = gfn_getUserGroup();											//사용자 그룹을 가져온다


//	DEBUG("fn_query", "ds_cond is " + ds_cond.SaveXml());

	ufn_clearGrid();
	gfn_clearMsgBar();

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps01:PS0103001A_S1", true, true);  //==> CsvSaerch로 변경		
	tit_addCsvSearchActionInfo("ps01:PS0103001A_S1");

	fn_createWait("",this);														//"처리중입니다. 잠시 기다려주십시오." 윈도우 create

	//grd_list_detail.visible = false;


	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list_master=ds_list_master"
		, ""
		, "fn_afterQueryMaster"
		, ""
		, ""
		, true);        




}

