/********************************************************************************
* 기      능   : 그리드를 초기화한다.
********************************************************************************/
function ufn_clearGrid()
{
	gfn_clearGridSortMark(grd_list_master); 
	gfn_clearGridSortMark(grd_list_detail);
	ds_list_master.Clear();
	ds_list_detail.Clear();
}

/********************************************************************************
* 기      능   : 검색 조건들을 초기화 한다
********************************************************************************/
function ufn_initSearchParameters()
{
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    var codeArr = ["PS003"];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);


	//검색조건 Combo Initialize	
    cb_stat.Index = 0;
	cb_gubun.Index = 0;
	cb_gbn.Index = 0;
	//cb_zzactss.Index = 0;


	var currYear = left(today(), 4);
	cal_to.Value = today();
	cal_from.Value = gfn_getBeforeDate(cal_to.Value, 100);

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps01:PS0103001A_S4");    //==> CsvSaerch로 변경
	tit_addCsvSearchActionInfo("ps01:PS0103001A_S4"); 	

	tit_callService(
		""
		, ""
		, ""
		, "ds_sendid=ds_sendid"
		, ""
		, ""
		, ""  
		, ""
		, true);

	gfn_insertEmptyRow( ds_sendid, G_CODE_ALL );

	//ds_sendid.insertRow(0);
	//ds_sendid.setColumn(0, "CODE", "null");
	//ds_sendid.setColumn(0, "CODE_NAME", gfn_getCodeName(G_CODE_ALL));

	cb_sendid.Index = 1;



}

