}





/*******************************************************************************
* 기      능   : 설문 상세 정보 함수
*******************************************************************************/
function ufn_display(obj,row)
{
	ds_cond2.ClearData();
	ds_cond2.AddRow();	
	ds_cond2.SetColumn(0,"PSPID", obj.GetColumn(row, "PSPID") );
	ds_cond2.SetColumn(0,"RESPONDENT", obj.GetColumn(row, "RESPONDENT") );
	ds_cond2.SetColumn(0,"SURVEYGBN", obj.GetColumn(row, "SURVEYGBN") );
	ds_cond2.SetColumn(0,"TEMNO", obj.GetColumn(row, "TEMNO") );
	ds_cond2.SetColumn(0,"SURVEYID", obj.GetColumn(row, "SURVEYID") );

	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps01:PS0103001A_S2", false, true);					//Master List에 대한 Detail List
	tit_addSearchActionInfo("ps01:PS0103001A_S3", false, true);					//Detail List에 대한 응답List

	tit_callService(
		""
		, ""
		, "ds_cond2=ds_cond2"
		, "ds_list_detail=ds_list_detail ds_list_answer=ds_list_answer"
		, ""
		, ""  //"fn_afterQueryMaster"
		, ""
		, ""
		, true);    


	var mrow = 0;
	var mapping_cctn_txt = "";
	ds_answer_mapping.ClearData();	


	for (var i=0; i< ds_list_answer.count; i++)
	{	
		if (ds_list_answer.GetColumn(i, "MANY") == 'X')
		{
			var hnos = Split(ds_list_answer.GetColumn(i, "ANSWER"), ",");			
			for(var y=0; y< length(hnos); y++)
			{				

				mrow = ds_answer_mapping.AddRow();	
				ds_answer_mapping.SetColumn(mrow, "SURVEYGBN", ds_list_answer.GetColumn(i, "SURVEYGBN") );
				ds_answer_mapping.SetColumn(mrow, "SURVEYID", ds_list_answer.GetColumn(i, "SURVEYID") );
				ds_answer_mapping.SetColumn(mrow, "QUESTNO", ds_list_answer.GetColumn(i, "QUESTNO") );
				ds_answer_mapping.SetColumn(mrow, "ANSWER", hnos[y] );

				mapping_cctn_txt = ds_list_answer.GetColumn(i, "SURVEYGBN") + ds_list_answer.GetColumn(i, "SURVEYID") +
				                   ds_list_answer.GetColumn(i, "QUESTNO")   + hnos[y] ;
				ds_answer_mapping.SetColumn(mrow, "CCTN_TXT", mapping_cctn_txt );				
			}									
		}
		else
		{	
			mrow = ds_answer_mapping.AddRow();	
			ds_answer_mapping.SetColumn(mrow, "SURVEYGBN", ds_list_answer.GetColumn(i, "SURVEYGBN") );
			ds_answer_mapping.SetColumn(mrow, "SURVEYID", ds_list_answer.GetColumn(i, "SURVEYID") );
			ds_answer_mapping.SetColumn(mrow, "QUESTNO", ds_list_answer.GetColumn(i, "QUESTNO") );
			if (ds_list_answer.GetColumn(i, "TYPE") == 'T')
			{	
				ds_answer_mapping.SetColumn(mrow, "ANSWER", ds_list_answer.GetColumn(i, "ANSWERT") );	

				mapping_cctn_txt = ds_list_answer.GetColumn(i, "SURVEYGBN") + ds_list_answer.GetColumn(i, "SURVEYID") +
				                   ds_list_answer.GetColumn(i, "QUESTNO")  ;
				ds_answer_mapping.SetColumn(mrow, "CCTN_TXT", mapping_cctn_txt );
			}
			else if (ds_list_answer.GetColumn(i, "TYPE") == 'I')
			{
				ds_answer_mapping.SetColumn(mrow, "ANSWER", ds_list_answer.GetColumn(i, "ANSWER") );

				mapping_cctn_txt = ds_list_answer.GetColumn(i, "SURVEYGBN") + ds_list_answer.GetColumn(i, "SURVEYID") +
				                   ds_list_answer.GetColumn(i, "QUESTNO")  ;
				ds_answer_mapping.SetColumn(mrow, "CCTN_TXT", mapping_cctn_txt );
			}			
			else if (ds_list_answer.GetColumn(i, "TYPE") == 'M')
			{
				ds_answer_mapping.SetColumn(mrow, "ANSWER", ds_list_answer.GetColumn(i, "ANSWER") );

				mapping_cctn_txt = ds_list_answer.GetColumn(i, "SURVEYGBN") + ds_list_answer.GetColumn(i, "SURVEYID") +
				                   ds_list_answer.GetColumn(i, "QUESTNO")   + ds_list_answer.GetColumn(i, "ANSWER");
				ds_answer_mapping.SetColumn(mrow, "CCTN_TXT", mapping_cctn_txt );

			}
		}	
	}


	var detali_cctn_txt = "";	
	for (var i=0; i<ds_list_detail.count; i++)
	{
		if(ds_list_detail.GetColumn(i, "LEVEL") == '3')
		{

			if(ds_list_detail.GetColumn(i, "TYPE") == 'M')
			{
				detali_cctn_txt = ds_list_detail.GetColumn(i, "SURVEYGBN") + ds_list_detail.GetColumn(i, "SURVEYID") +
				                  ds_list_detail.GetColumn(i, "QUESTNO")   + ds_list_detail.GetColumn(i, "QUESTSEQ") ;

				if(ds_answer_mapping.FindRow("CCTN_TXT", detali_cctn_txt) != -1 )                  
				{

					ds_list_detail.SetColumn(i, "ANSWER", "1");
				}

			}
			else
			{
				detali_cctn_txt = ds_list_detail.GetColumn(i, "SURVEYGBN") + ds_list_detail.GetColumn(i, "SURVEYID") +
				                  ds_list_detail.GetColumn(i, "QUESTNO")    ;

				if(ds_answer_mapping.FindRow("CCTN_TXT", detali_cctn_txt) != -1 )  
				{
					ds_list_detail.SetColumn(i, "ANSWER",  ds_answer_mapping.GetColumn(ds_answer_mapping.FindRow("CCTN_TXT", detali_cctn_txt), "ANSWER" ) );                  
				}
			}

		}



	}




}
