function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	/*
	if(G_USER_ID != '2029847')
	{
		alert("설치 가시성 강화 프로젝트로 인한 전산 작업 중입니다.\n2019-02-20 18:00 ~ 19:00 [예정]\n정보기술팀 송광용 DR문의");
		close();
		return;
	}
	*/


	fv_gbn = s_gbn;
   //PM지정일 : 현재월의 첫날짜, 현재날짜
	var firstDay = substr(gfn_getCurrDate(),0,6)+"01";
	var lastDay = gfn_getCurrDate();
	// INTERFACE 이력이 없을 시 EVENT 처리

	if(s_inter <> 'X')
	{	 
	   ed_pspid.Text = s_pspid;
	   cb_mode.Index = 0;
	   //Tab00.TabIndex = 3;
	}
	else
	{
		ed_pspid.Text = s_pspid ;
		cb_mode.Index = 1;
    }


	// #화면 좌측 출하요청일 및 Capa 일자 설정. 금일+1일 ~ 현재월 + 3개월의 마지막날
	ds_cond.fireevent = false;
	//오늘 +1일
	//현재월+3개월의 마지막날 gfn_getLeapLastDay(date)
	/*
	trace("오늘+1일 : "+gfn_getAfterDate(gfn_getCurrDate(),1));
	trace("현재월 : "+gfn_getCurrMonth()); // 201810
	trace("현재월 +3개월 : "+gfn_getAfterMonth(gfn_getCurrMonth(),3));
	trace("현재월+3개월의 마지막날 : "+gfn_getLeapLastDay(gfn_getAfterMonth(gfn_getCurrMonth(),3)+'01'));
	*/

	ds_cond.SetColumn(0, "I_FDATE", gfn_getAfterDate(gfn_getCurrDate(),1));	//오늘 + 1일
	ds_cond.SetColumn(0, "I_TDATE", gfn_getLeapLastDay(gfn_getAfterMonth(gfn_getCurrMonth(),3)+'01'));	// 현재월 + 3개월의 마지막 날
	ds_cond.fireevent = true;


	// 설치 준공 확정 권한 체크(2016.04.12 추가--LEH)
	// 착공일자별 로드분석 조회 처리 
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S71");
	tit_addSearchActionInfo("ps02:PS0201001A_S72");
	tit_addSearchActionInfo("ps02:PS0201001A_S73");
	tit_addSearchActionInfo("ps02:PS0201001A_S74"); // SRM 권한 ZPS29(설치관리팀) 확인위해
	tit_addSearchActionInfo("ps02:PS0201001A_S76"); // SRM 권한 영업대리점 (zsd11, zsdb10) 확인위해
	tit_callService(
      ""
	, ""
	, "ds_cond_cd=ds_cond_cd ds_cond=ds_cond"
	, "ds_autal=ds_autal ds_list_l=ds_list_l ds_authChk=ds_authChk ds_authChk2=ds_authChk2 ds_sdAuth=ds_sdAuth"
	, ""
	, "fn_auth"
	, true,true);






	/*
	tit_callService(
        "PP0102030A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list_l=ds_list"
        , ""
        , "fn_afterAct"
        , false);
    */

}
function fn_afterQuery1() {}
/********************************************************************************
* 기      능   :  권한 체크 
********************************************************************************/
function fn_auth()
{   
	if(ds_authChk2.GetColumn(0, "AUTH_CNT") == 0)
	{
		F_CHG_AUTH = "";
	}
	else
	{
		F_CHG_AUTH = "Y";
	}

     //설치 준공 확정 담당자 권한 부여 
    if(ds_autal.GetColumn(0,"AUTAL") == 'X')
    {  //alert("AA");
       //userGroup = '40';
    }


    // SD(대리점 권한 별도) - 저장/삭제는 안되지만, 조회는 전체 가능하도록 세팅.
    if(ds_sdAuth.GetColumn(0, "AUTH_CNT") > 0)
    {
		btn_delete.Visible = false;
		btn_save.Visible = false;
		userGroup = '40';
    }

    //권한 별 활성/비활성 
  	if( userGroup == '10' )
	{
	    chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;

		Tab00.tab3.grd_list.Format = "Default";
		Tab00.tab4.grd_list.Format = "Default";
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S11");
		tit_addSearchActionInfo("ps02:PS0201001A_S51");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond_cd"
			, "ds_zzlifnr_cd=ds_zzlifnr_cd ds_gubun=ds_gubun"
			, ""
			, "fn_afterZzlifnrSearch"
			, true,true);
	}
	else if( userGroup == '30' )
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;

		Tab00.tab3.grd_list.Format = "Default30";
		Tab00.tab4.grd_list.Format = "Default30";
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S31");
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_addSearchActionInfo("ps02:PS0201001A_S33");
		tit_addSearchActionInfo("ps02:PS0201001A_S51");
		tit_callService(
			""
			, ""
			, "ds_cond_cd=ds_cond_cd"
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd ds_zzlifnr_cd=ds_zzlifnr_cd ds_gubun=ds_gubun"
			, ""
			, "fn_afterZzactssSearch"
			, true,true);
	}
	else if( userGroup == '40' )
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;

		Tab00.tab3.grd_list.Format = "Default40";
		Tab00.tab4.grd_list.Format = "Default40";
		//코드콤보 설정 : 지역, 구분2
		var codeArr = ["PS003"];
		var dsObjArr = [ds_zzactss_cd];
		var addTypeArr = [G_CODE_ALL];
		gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S33");
		tit_addSearchActionInfo("ps02:PS0201001A_S51");
		tit_callService(
			""
			, ""
			, "ds_cond_cd=ds_cond_cd"
			, "ds_zzlifnr_cd=ds_zzlifnr_cd ds_gubun=ds_gubun"
			, ""
			, "fn_afterSearchAll"
			, true,true);

	}

	//권한에 따라 조회조건 필드 활성화/비활성화
	fn_authCheck('0');
	btn_gis.Enable = false;
	/*
	if ( G_USER_ID != '2018900' )
	{	

		btn_naver.Enable 	= false;
		btn_naver.Visible	= false;

	}
*/
	gfn_showMsg("CI00007");



	// 조회처리 gbn = "SELECT"
	if(fv_gbn == "SELECT")
	{
		var nCnt = ds_authChk.GetColumn(0, "AUTH_CNT");

		if(nCnt == 0) // 설치권한이 아니면
		{
			btn_delete.Visible = false;
			btn_save.Visible = false;
			//Tab00.tab2.btn_subSave.Visible = false;
		}



		Tab00.TabIndex = 1;
		ed_pspid.Text = s_pspid ;
		fn_query();
	}
	else if(fv_gbn == "CHULHA")
	{
		var nCnt = ds_authChk.GetColumn(0, "AUTH_CNT");

		if(nCnt == 0) // 설치권한이 아니면
		{
			btn_delete.Visible = false;
			btn_save.Visible = false;
			//Tab00.tab2.btn_subSave.Visible = false;
		}
		else
		{

				btn_save.Enable = true;

		}

		Tab00.TabIndex = 0;
		cb_mode.Index = 1;
		ed_pspid.Text = s_pspid;
		fn_query();
	}
	else if(fv_gbn == "INPUT")
	{
		var nCnt = ds_authChk.GetColumn(0, "AUTH_CNT");

		if(nCnt == 0) // 설치권한이 아니면
		{
			btn_delete.Visible = false;
			btn_save.Visible = false;
			//Tab00.tab2.btn_subSave.Visible = false;
		}
		else
		{

				btn_save.Enable = true;


		}

		Tab00.TabIndex = 2;
		cb_mode.Index = 0;
		ed_pspid.Text = s_pspid;
		fn_query();
	}
	else
	{
		Tab00.TabIndex = 0;
	}

}

function fn_authCheck(flg)
{
	if(flg == '0')
	{
		ufn_enableBtn("query", true);
		ufn_enableBtn("save", false);
		ufn_enableBtn("delete", false);
		cal_datef.Enable = true;
		cal_datet.Enable = true;
		ed_pspid.Enable = true;
		cb_mode.Enable = true;
		if( userGroup == '10' )
		{
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = false;
			cb_zzlifnr.Enable = false;
		}
		else if( userGroup == '30' )
		{
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = true;
			cb_zzlifnr.Enable = true;
		}
		else if( userGroup == '40' )
		{
			cb_zzactss.Enable = true;
			cb_zzpmnum.Enable = true;
			cb_zzlifnr.Enable = true;
		}
		else
		{
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = false;
			cb_zzlifnr.Enable = false;
		}
	}
	else
	{
		cal_datef.Enable = false;
		cal_datet.Enable = false;
		ed_pspid.Enable = false;
		cb_mode.Enable = false;
		cb_zzactss.Enable = false;
		cb_zzpmnum.Enable = false;
		cb_zzlifnr.Enable = false;
	}
}
/********************************************************************************
* 기      능   : 사무소콤보 변경시 설치PM 콤보값 생성
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

if(userGroup == "40" || userGroup == "30"){
	ds_cond.SetColumn(0,"ZZLIFNR",'');  }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_zzlifnr_cd.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_zzlifnr_cd.Filter("ZZACTSS ==" +quote(arr));
    }


	ds_cond_cd.SetColumn(0,"ZZACTSS",strCode);
	cb_zzpmnum.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S41");
	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond_cd"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, "fn_afterZzactssChange"
		, true,true);
}
