/********************************************************************************
* 기      능   :  출하물량 집중, 요청 일자 불가
********************************************************************************/
function fn_checkLoadDay()
{
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S1");      

	tit_callService(
		""
		, ""
		, "ds_cond_day=ds_cond_day"
		, "ds_chk=ds_chk"
		, ""
		, ""
		, ""  //true
		, ""
		, true);


	if( ds_chk.GetColumn(0, "CNT") = 0 )
	{
		return false;
	}
	else
	{
		return true;
	}


}




/********************************************************************************
* 기      능   : 영업일체크(rfc)
********************************************************************************/
function fn_checkWorkDay()
{
	// 2021-07-01 신성철 직장 요청. 특근진행으로 인한 7월 30일 입력가능하도록 요청.
	if(Substr(ds_cond.GetColumn(0, "PSPID"),0,1) == 'J' || ds_cond_day.GetColumn(0, "I_CHECK_DATE") == '20210730' || ds_cond_day.GetColumn(0, "I_CHECK_DATE") == '20210923')
	{
		E_TYPE = 0;
		return;
	}

	// workday조회
	tit_callService(
        "COMM_ACT"
        , "getPsWorkingDay"
        , ""
        , ""
        , "I_CHECK_DATE=" + quote(ds_cond_day.GetColumn(0,"I_CHECK_DATE"))
        , "fn_aftercheckWorkDay"
        ,true,true,true);

}


/********************************************************************************
* 기      능   : 근무일 17일 이후
********************************************************************************/
function fn_WorkDay_17(spart,sp_ause)
{

    //신성철, 김항진 기장, 조용준 + 김도윤 추가(신성철씨요청)
	if(G_USER_ID == '3018598' or G_USER_ID == '2026490' or G_USER_ID == '2021757'or G_USER_ID == '3012751' or G_USER_ID == '2019094' or G_USER_ID == '2006227' or G_USER_ID == '3017044' or G_USER_ID == '3016552' or G_USER_ID == '3011534' or G_USER_ID == '3013383'or G_USER_ID == '3012255' or G_USER_ID == '3016196' or G_USER_ID == '2016885'or G_USER_ID == '3016200'  or spart == 'Y'
	    or G_USER_ID == '2026627'  or G_USER_ID == '2026660'  or G_USER_ID == '2026350' or sp_ause == 'Y' or G_USER_ID == '2028891'  or G_USER_ID == '2017601' or G_USER_ID == '2019191' or G_USER_ID == '2029847' or G_USER_ID == '2017946')//or G_USER_ID == '2022702') 
	{     //우제석대리의 요청으로 이무열BJ, 이대인JJ, 이병만JJ,이용범,윤휘중,김대희,이재현 추가
	      //우제석 대리 요청으로 박한님sw 추가
	      // 신성철씨 요청으로 신기성씨 추가 
	      // 신기성씨 요청으로 김택영씨 추가 
	      // 우제석 DR 영업으로 이동 -> 권한 삭제
          //김항진 기장 추가
          // 신기성씨 요청으로 신입사원 임세준 /김태환/김동훈 추가
          //우동훈 GJ 요청 추가 자재 17일 이전 선택 추가 
          // 김도윤sw요청으로 김민규 sw추가rk
          // 신성철 GJ요청으로 강주현 SW추가
		return true;
	}
	else
	{
		if( ToNumber(gfn_getWorkingDate(17,"1000",gfn_getCurrDate()))
		    <= ToNumber(ds_cond_day.GetColumn(0,"I_CHECK_DATE")) )
		{
			return true;
		}
		else
		{
			return false;
		}

	}



}


/********************************************************************************
* 기      능   : 저장(착준공대상 수정)
********************************************************************************/
function fn_save3() {
	if ( !gfn_isChangeDs(ds_list5, true, "")){
		return;
	}

	for( var i=0; i<ds_list5.rowcount; i++ )
	{
		if (ds_list5.GetColumn(i,"FLAG") == "I")
		{
			if(ds_list5.GetColumn(i,"ZZFKEYD") == '1')
			{
				ds_list5.SetColumn(i,"ZZFKEYD","X");
			}
			/*else if( ds_list5.GetColumn(i,"ZZFKEYD") == 'X')
			{
			    ds_list5.SetColumn(i,"ZZFKEYD","X");
			}*/	
			else
			{
				ds_list5.SetColumn(i,"ZZFKEYD","");
			}
			if(ds_list5.GetColumn(i,"ZZSTAN1") == '1')
			{
				ds_list5.SetColumn(i,"ZZSTAN1","X");
			}
		/*	else if( ds_list5.GetColumn(i,"ZZSTAN1") == 'X')
			{
			    ds_list5.SetColumn(i,"ZZSTAN1","X");
			}*/
			else
			{
				ds_list5.SetColumn(i,"ZZSTAN1","");
			}

			if((userGroup == "10" || userGroup == "30") &&
			    length(ds_list5.GetColumn(i,"ZZFKEYD")) == 0 && length(ds_list5.GetColumn(i,"ZZSTAN1")) == 0)
			{
				/*
				if ( length(ds_list5.getColumn(i, "ZZSHIP1")) == 0 || ds_list5.getColumn(i, "ZZSHIP1") == "0000-00-00") {
					gfn_showMsg("CW00001",st_zzship1.value);
					ds_list5.row = i;
					return;
				}
				*/
				if ( length(ds_list5.getColumn(i, "AUFNR1")) > 0 && 
				    ( length(ds_list5.getColumn(i, "ZZSHIP1")) == 0 || ds_list5.getColumn(i, "ZZSHIP1") == "0000-00-00")) {
					gfn_showMsg("CW00001",st_zzship1.value);
					ds_list5.row = i;
					return;
				}

				else if ( length(ds_list5.getColumn(i, "AUFNR2")) > 0 &&
						 (length(ds_list5.getColumn(i, "ZZSHIP2")) == 0 || ds_list5.getColumn(i, "ZZSHIP2") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship2.value);
					ds_list5.row = i;
					return;
				}
				else if ( length(ds_list5.getColumn(i, "AUFNR3")) > 0 &&
						 (length(ds_list5.getColumn(i, "ZZSHIP3")) == 0 || ds_list5.getColumn(i, "ZZSHIP3") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship3.value);
					ds_list5.row = i;
					return;
				}
				else if ( length(ds_list5.getColumn(i, "AUFNR4")) > 0 &&
						  (length(ds_list5.getColumn(i, "ZZSHIP4")) == 0 || ds_list5.getColumn(i, "ZZSHIP4") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship4.value);
					ds_list5.row = i;
					return;
				}
				else if ( length(ds_list5.getColumn(i, "AUFNR5")) > 0 &&
						 (length(ds_list5.getColumn(i, "ZZSHIP5")) == 0 || ds_list5.getColumn(i, "ZZSHIP5") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship5.value);
					ds_list5.row = i;
					return;
				}
				else if ( length(ds_list5.getColumn(i, "AUFNR6")) > 0 &&
						 (length(ds_list5.getColumn(i, "ZZSHIP6")) == 0 || ds_list5.getColumn(i, "ZZSHIP6") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship6.value);
					ds_list5.row = i;
					return;
				}
			}
			else if(userGroup == "40" && length(ds_list5.GetColumn(i,"ZZCHAKG")) == 0 &&
			        length(ds_list5.GetColumn(i,"ZZJUNGO")) == 0)
			{
			    /*
				if ( length(ds_list5.getColumn(i, "ZZSHIP1")) == 0 || ds_list5.getColumn(i, "ZZSHIP1") == "0000-00-00") {
					gfn_showMsg("CW00001",st_zzship1.value);
					ds_list5.row = i;
					return;
				}
				*/
				if ( length(ds_list5.getColumn(i, "AUFNR1")) > 0 &&
				    ( length(ds_list5.getColumn(i, "ZZSHIP1")) == 0 || ds_list5.getColumn(i, "ZZSHIP1") == "0000-00-00")) {
					gfn_showMsg("CW00001",st_zzship1.value);
					ds_list5.row = i;
					return;
				}

				else if ( length(ds_list5.getColumn(i, "AUFNR2")) > 0 &&
						 (length(ds_list5.getColumn(i, "ZZSHIP2")) == 0 || ds_list5.getColumn(i, "ZZSHIP2") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship2.value);
					ds_list5.row = i;
					return;
				}
				else if ( length(ds_list5.getColumn(i, "AUFNR3")) > 0 &&
						 (length(ds_list5.getColumn(i, "ZZSHIP3")) == 0 || ds_list5.getColumn(i, "ZZSHIP3") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship3.value);
					ds_list5.row = i;
					return;
				}
				else if ( length(ds_list5.getColumn(i, "AUFNR4")) > 0 &&
						  (length(ds_list5.getColumn(i, "ZZSHIP4")) == 0 || ds_list5.getColumn(i, "ZZSHIP4") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship4.value);
					ds_list5.row = i;
					return;
				}
				else if ( length(ds_list5.getColumn(i, "AUFNR5")) > 0 &&
						 (length(ds_list5.getColumn(i, "ZZSHIP5")) == 0 || ds_list5.getColumn(i, "ZZSHIP5") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship5.value);
					ds_list5.row = i;
					return;
				}
				else if ( length(ds_list5.getColumn(i, "AUFNR6")) > 0 &&
						 (length(ds_list5.getColumn(i, "ZZSHIP6")) == 0 || ds_list5.getColumn(i, "ZZSHIP6") == "0000-00-00" )) {
					gfn_showMsg("CW00001",st_zzship6.value);
					ds_list5.row = i;
					return;
				}
			}

			//준공예정일 체크
			if(replace(ds_list5.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list5.getColumn(i, "ZZSHIP1"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list5.row = i;
				return;
			}
			else if(replace(ds_list5.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list5.getColumn(i, "ZZSHIP2"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list5.row = i;
				return;
			}
			else if(replace(ds_list5.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list5.getColumn(i, "ZZSHIP3"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list5.row = i;
				return;
			}
			else if(replace(ds_list5.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list5.getColumn(i, "ZZSHIP4"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list5.row = i;
				return;
			}
			else if(replace(ds_list5.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list5.getColumn(i, "ZZSHIP5"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list5.row = i;
				return;
			}

			if( userGroup == '30' && length(ds_list5.getColumn(i, "ZZPMNUM")) == 0)
			{
				gfn_showMsg("CW00001",st_zzpmnum.value);
				ds_list5.row = i;
				return;
			}
			else if( userGroup == '30' && length(ds_list5.getColumn(i, "ZZLIFNR")) == 0)
			{
				gfn_showMsg("CW00001",st_zzlifnr.value);
				ds_list5.row = i;
				return;
			}
			/*else if( userGroup == '30' && length(ds_list5.getColumn(i, "ZZLIFNR2")) == 0)
			{
				gfn_showMsg("CW00001",st_zzlifnr2.value);
				ds_list5.row = i;
				return;
			}*/
			else if( userGroup == '40' && length(ds_list5.getColumn(i, "ZZACTSS")) == 0)
			{
				gfn_showMsg("CW00001",st_zzactss.value);
				ds_list5.row = i;
				return;
			}
			else if( userGroup == '40' && length(ds_list5.getColumn(i, "ZZPMNUM")) == 0)
			{
				gfn_showMsg("CW00001",st_zzpmnum.value);
				ds_list5.row = i;
				return;
			}
			else if( userGroup == '40' && length(ds_list5.getColumn(i, "ZZLIFNR")) == 0)
			{
				gfn_showMsg("CW00001",st_zzlifnr.value);
				ds_list5.row = i;
				return;
			}
		/*	else if( userGroup == '40' && length(ds_list5.getColumn(i, "ZZLIFNR2")) == 0)
			{
				gfn_showMsg("CW00001",st_zzlifnr2.value);
				ds_list5.row = i;
				return;
			}*/

		} //if
	} //for

	//jcbc : 양중업체 팀장 UPDATE 위해 데이타셋 생성
	var rows = 0;
	for(var j=0; j<ds_list5.rowcount; j++)
	{ 
		if(ds_list5.getcolumn(j,"FLAG") == "I")
		{
			if(ds_list5.getcolumn(j,"ZZFKEYD") == '1')
			{
				global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
				global.FRM_BOTTOM.st_msg.color = "yellow";       
				global.FRM_BOTTOM.st_msg.value = "착공 F 저장 시 문제 발생 현장입니다. 정보기술부 이은혜사원에게 문의하세요.";
				return false;	  
			}
			else if(ds_list5.getcolumn(j,"ZZSTAN1") == '1')
			{
			    global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
				global.FRM_BOTTOM.st_msg.color = "yellow";       
				global.FRM_BOTTOM.st_msg.value = "준공 F 저장 시 문제 발생 현장입니다. 정보기술부 이은혜사원에게 문의하세요.";
				return false;	
			}

			ds_list5_1.AddRow();
			ds_list5_1.CopyRow(rows, ds_list5, j);
			rows++;

		}

	//	(ds_list5_1.SaveXML());
	}

	// 저장 처리
	tit_callService(
        "PS0201001A_ACT"
        , "save"
        , "ds_list=ds_list5:A ds_cond=ds_cond_bak"
        , "ds_error=ds_error ds_list5=ds_list"
        , "GUBUN='U'"
        , "fn_afterSave3");

}

