********************************************************************************/
function fn_excel() {
	if(Tab00.TabIndex == 0) {
		gfn_saveToExcel(Tab00.tab1.grd_list, true );
	} else if(Tab00.TabIndex == 2) {
		gfn_saveToExcel(Tab00.tab3.grd_list, true );
	} else if(Tab00.TabIndex == 3) {
		gfn_saveToExcel(Tab00.tab4.grd_list, true );
	}
}
