// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리
	gfn_showSearchMsg(Tab00.tab1.grd_list, errCode, errMsg);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S75");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_master=ds_master"
		, ""
		, "fn_afterMaster"
		, true);

}

/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterMaster(errCode, errMsg) {
	ds_cond_bak.copy(ds_cond);

	pspid = ds_cond.getcolumn(0,"PSPID");
	actss = ds_cond.getcolumn(0,"ZZACTSS");
	pmnum = ds_cond.getcolumn(0,"ZZPMNUM");
	lifnr = ds_cond.getcolumn(0,"ZZLIFNR");

	if(fv_gbn == "CHULHA")
	{
		Tab00.TabIndex = 3;
		fn_query();
	}
}
