/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list3.RowCount+"");

	// 현장 여건 준비 상태 테이블에 데이터가 존재하지 않으면, addRow처리

	if(ds_chkStus.rowcount == 0)
	{
		ds_chkStus.ClearData();
		ds_chkStus.addRow();
		ds_chkStus.SetColumn(0, "PSPID", ds_cond2.GetColumn(0, "PSPID"));
		ds_chkStus.SetColumn(0, "COMPLT", "A");

		ds_chkStus.SetColumn(0, "CHK1", "0");
		ds_chkStus.SetColumn(0, "CHK2", "0");	
		ds_chkStus.SetColumn(0, "CHK3", "0");	
		ds_chkStus.SetColumn(0, "CHK4", "0");	
		ds_chkStus.SetColumn(0, "CHK5", "0");	
		// 이현용 책임 요청으로 새로 추가 20210824 
		ds_chkStus.SetColumn(0, "CHK6", "0");	
		ds_chkStus.SetColumn(0, "CHK7", "0");	
		ds_chkStus.SetColumn(0, "CHK8", "0");	
		ds_chkStus.SetColumn(0, "CHK9", "0");	
	}

	// 자동기록 Filter
	if(Tab00.tab2.chk_auto.Value == '1')
	{
		ds_list3.Filter("");
	}
	else
	{
		ds_list3.Filter("INPT == 'A'");
	}

	/*if(ds_list3.rowcount == 0)
	{
		ds_list3.AddRow();
		ds_list3.SetColumn(0,"PSPID",ds_list2.GetColumn(0,"POSID"));
		//if(ds_cellp.RowCount() > 0)
		//ds_list3.SetColumn(0,"CELLP",ds_cellp.GetColumn(0,"CELLP"));
		inUpFlg = "I";
	}
	else {

	  inUpFlg = "U";
	}//*김연식 대리가 핀제거 막아달라고 요청함.
	//fn_pin();
	*/
}

/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery3(errCode, errMsg) {
	// 메시지 처리
	gfn_showSearchMsg(Tab00.tab3.grd_list, errCode, errMsg);

	ds_cond_bak.copy(ds_cond);
	//Trace(ds_list4.saveXML());

	if( userGroup == '10' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001C_S11");
		tit_addSearchActionInfo("ps02:PS0201001B_S9");  //PS0201001B_S2에서 PS0201001B_S9로변경 (착준공대상 조회 NEW에서)
		tit_addSearchActionInfo("ps02:PS0201001X_S5");
		tit_addSearchActionInfo("ps02:PS0201001E_S7");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zztemno_grd_cd=ds_zztemno_grd_cd ds_list6=ds_list3 ds_list9=ds_list9 ds_leadtime=ds_leadtime"
			, ""
			, ""
			, true,true);
	}
	else if( userGroup == '30' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001C_S31");
		tit_addSearchActionInfo("ps02:PS0201001C_S32");
		tit_addSearchActionInfo("ps02:PS0201001C_S33");
		tit_addSearchActionInfo("ps02:PS0201001B_S9");  //PS0201001B_S2에서 PS0201001B_S9로변경 (착준공대상 조회 NEW에서)
		tit_addSearchActionInfo("ps02:PS0201001X_S5");
		tit_addSearchActionInfo("ps02:PS0201001E_S7");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzpmnum_grd_cd=ds_zzpmnum_grd_cd ds_zzlifnr_grd_cd=ds_zzlifnr_grd_cd ds_zztemno_grd_cd=ds_zztemno_grd_cd ds_list6=ds_list3 ds_list9=ds_list9 ds_leadtime=ds_leadtime"
			, ""
			, "fn_afterPmnum"
			, true,true);
	}
	else if( userGroup == '40' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001C_S41");
		tit_addSearchActionInfo("ps02:PS0201001C_S42");
		tit_addSearchActionInfo("ps02:PS0201001C_S43");
		tit_addSearchActionInfo("ps02:PS0201001C_S44");
		tit_addSearchActionInfo("ps02:PS0201001B_S9");  //PS0201001B_S2에서 PS0201001B_S9로변경 (착준공대상 조회 NEW에서)
		tit_addSearchActionInfo("ps02:PS0201001X_S5");
		tit_addSearchActionInfo("ps02:PS0201001E_S7");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzactss_grd_cd=ds_zzactss_grd_cd ds_zzpmnum_grd_cd=ds_zzpmnum_grd_cd  ds_zzlifnr_grd_cd=ds_zzlifnr_grd_cd ds_zztemno_grd_cd=ds_zztemno_grd_cd ds_list6=ds_list3 ds_list9=ds_list9 ds_leadtime=ds_leadtime"
			, ""
			, "fn_ZZACTSS"
			, true,true);
	}

	ds_temp.ClearData();
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S77");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_temp=ds_temp"
		, ""
		, ""
		, ""
		, ""
		, true);
	//Trace(ds_leadTime.saveXML());

	var nRow = -1;
	ds_list4.AddColumn("ZZILSIC", "STRING", 255);
	for(var i=0;i<ds_list4.GetRowCount();i++)
	{
		nRow = ds_temp.SearchRow("POSID_1 == '"+ds_list4.GetColumn(i, "POSID")+"'");
		if(nRow >= 0)
		{
			ds_list4.SetColumn(i, "ZZILSIC", ds_temp.GetColumn(nRow, "ZZILSIC"));
			//ds_list4.SetColumn(i, "CHK", "0");
		}
	}
	//Trace(ds_temp.saveXML());
	//Trace(ds_list4.saveXML());
}



/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery4(errCode, errMsg) {
	// 메시지 처리

	gfn_showSearchMsg(Tab00.tab4.grd_list, errCode, errMsg);
	ds_cond_bak.copy(ds_cond);

	ds_list5.CanRowPosChange = "ds_list5_CanRowPosChange";

	if( userGroup == '10' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001C_S11");
		tit_addSearchActionInfo("ps02:PS0201001E_S7");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zztemno_grd_cd=ds_zztemno_grd_cd ds_leadtime=ds_leadtime"
			, ""
			, ""
			, true,true);
	}
	else if( userGroup == '30' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001C_S31");
		tit_addSearchActionInfo("ps02:PS0201001C_S32");
		tit_addSearchActionInfo("ps02:PS0201001C_S33");
		tit_addSearchActionInfo("ps02:PS0201001E_S7");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzpmnum_grd_cd=ds_zzpmnum_grd_cd ds_zzlifnr_grd_cd=ds_zzlifnr_grd_cd ds_zztemno_grd_cd=ds_zztemno_grd_cd ds_leadtime=ds_leadtime"
			, ""
			, "fn_afterPmnum"
			, true,true);
	}
	else if( userGroup == '40' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001C_S41");
		tit_addSearchActionInfo("ps02:PS0201001C_S42");
		tit_addSearchActionInfo("ps02:PS0201001C_S43");
		tit_addSearchActionInfo("ps02:PS0201001C_S44");
		tit_addSearchActionInfo("ps02:PS0201001E_S7");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzactss_grd_cd=ds_zzactss_grd_cd ds_zzpmnum_grd_cd=ds_zzpmnum_grd_cd ds_zzlifnr_grd_cd=ds_zzlifnr_grd_cd ds_zztemno_grd_cd=ds_zztemno_grd_cd ds_leadtime=ds_leadtime"
			, ""
			, "fn_ZZACTSS"
			, true,true);
	}

	ds_temp.ClearData();
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S77");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_temp=ds_temp"
		, ""
		, ""
		, ""
		, ""
		, true);
	//Trace(ds_leadTime.saveXML());

	var nRow = -1;
	ds_list5.AddColumn("ZZILSIC", "STRING", 255);
	for(var i=0;i<ds_list5.GetRowCount();i++)
	{
		nRow = ds_temp.SearchRow("POSID_1 == '"+ds_list5.GetColumn(i, "POSID")+"'");
		if(nRow >= 0)
		{
			ds_list5.SetColumn(i, "ZZILSIC", ds_temp.GetColumn(nRow, "ZZILSIC"));
			//ds_list5.SetColumn(i, "CHK", "0");
		}
	}
	//Trace(ds_leadtime.saveXML());
}

