/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리
********************************************************************************/
function ds_list4_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	var sDate = replace(ds_list4.GetColumn(nRow,"SDATE"),'-','')+"(수주일자)";
	var zzship = st_zzship.Value;
	var zzcomp2 = st_zzcomp2.Value;
	var spart = substr(ds_list4.GetColumn(nRow, "POSID"),6,1);
	var sp_ause = ds_list4.GetColumn(nRow, "SP_AUSE");  // 추각자재 건 추가 
	var sBestDt1      = ds_list4.GetColumn(nRow, "BEST_DT1");
	var sBestDt2      = ds_list4.GetColumn(nRow, "BEST_DT2");
	var sBestDt3      = ds_list4.GetColumn(nRow, "BEST_DT3");
	var sBestDt4      = ds_list4.GetColumn(nRow, "BEST_DT4");
	var sBestDt5      = ds_list4.GetColumn(nRow, "BEST_DT5");

	// G1 완료가 아닐 시에 착공입력 불가 처리(2020-03-25 신성철 직장 요청)

	switch( strColumnID ) {
		case "CHK":
			break;
		case "ZZACTSS":	// 사무소 선택시
			ds_list4.SetColumn(nRow,"ZZPMNUM","");	// 설치PM 초기화
			ds_list4.SetColumn(nRow, "ZZLIFNR", "");	// 협력업체 초기화
			fn_changeZzpmun_grd_cd(ds_list4.GetColumn(nRow,strColumnID));
			break;
		case "ZZLIFNR":
			ds_list4.SetColumn(nRow,"ZZTEMNO","");
			fn_changeZztemno_grd_cd(ds_list4.GetColumn(nRow,strColumnID));
			break;
		case "ZZLIFNR2":
			ds_list4.SetColumn(nRow,"ZZTEMNO2","");
			fn_changeZztemno_grd_cd(ds_list4.GetColumn(nRow,strColumnID));
			break;	
		case "ZZSHIP1":
			// 최적출하가능일 비교 - 출하요청일 < 최적출하가능일 일 경우 통제기능 추가 - 2020.03.25 신성철 직장 요청.
			if(sBestDt1 > ds_list4.getcolumn(nRow,"ZZSHIP1") && Substr(ds_list4.GetColumn(nRow, "PSPID"),0,1) != 'J' && Substr(ds_list4.GetColumn(nRow, "POSID"),6,1) == 'L')
			{
				var msg = "최적출하가능일이 "+substr(sBestDt1,4,2)+"/"+substr(sBestDt1,6,2)+"일 입니다.\n이 날짜 이후로 저장해주세요.\n일정 단축 필요시 생산관리팀 연락 바랍니다.";
				gfn_openMsgWin(msg, "W", "A" );
				ds_list4.SetColumn(nRow,"ZZSHIP1",ds_list4.GetOrgColumn(nRow,"ZZSHIP1"));
				obj.fireevent = true;
				return;
			}

			if(replace(ds_list4.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list4.SetColumn(nRow,"ZZSHIP1",ds_list4.GetOrgColumn(nRow,"ZZSHIP1"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}

		    //DATASET변경시 FLAG가 'Y'인 경우 휴일체크 안함
			if(	N_COPY != "Y")
			{

				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list4.getcolumn(nRow,"ZZSHIP1"));
				fn_checkWorkDay();

				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list4.SetColumn(nRow,"ZZSHIP1",ds_list4.GetOrgColumn(nRow,"ZZSHIP1"));
					obj.fireevent = true;
					return;
				}

				//=========================출하물량 집중일에는 1분럭은 입력불가===================
				if(fn_checkLoadDay() == true && sp_ause <> 'Y' && Substr(ds_list4.GetColumn(nRow, "PSPID"),0,1) != 'J') //추가 자재의 경우 출가 통제일 CHECK 제외 
				{
					//gfn_showAlert("CW00244");
					var msg = "생산기획팀의 요청으로 출하를 통제한\n날짜입니다.\n다른 날짜를 선택하시거나 생산기획팀에\n문의 바랍니다.";
					gfn_openMsgWin(msg, "W", "A" );
					ds_list4.SetColumn(nRow,"ZZSHIP1",ds_list4.GetOrgColumn(nRow,"ZZSHIP1"));
					obj.fireevent = true;
					return;
				}


				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list4.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list4.SetColumn(nRow,"ZZSHIP1",ds_list4.GetOrgColumn(nRow,"ZZSHIP1"));
					obj.fireevent = true;
					return;
				}

				//===========================================================================//

				// 준공예정일 리드타임 추가 2020-11-30 우동훈 책임 요청
				if(ds_list4.getColumn(nRow, "ZZCHAKG") <> 'X' && Substr(ds_list4.GetColumn(nRow, "PSPID"),0,1) != 'J')
				{
					var nLTrow = ds_leadtime.SearchRow("POSID == '"+ds_list4.GetColumn(nRow, "POSID")+"'");
					if(nLTrow >= 0)
					{
						var sCalDate = gfn_getAfterDate(varNewValue,ds_leadtime.getColumn(nLTrow,"LEAD_TIME"));
						//Trace(ds_leadtime.getColumn(nLTrow,"LEAD_TIME"));
						//Trace(sCalDate);

						var sDay = GetDay(sCalDate); // 6 : 토요일, 0 : 일요일

						if(sDay == 6) // 토요일 + 2일
						{
							sCalDate = gfn_getAfterDate(sCalDate,2);
						}
						else if(sDay == 0) // 일요일 + 1일
						{
							sCalDate = gfn_getAfterDate(sCalDate,1);
						}

						obj.fireevent = false;
						ds_list4.SetColumn(nRow, "ZZCOMP2", sCalDate);
						obj.fireevent = true;
					}
				}
			}
			break;
		case "ZZSHIP2":
			// 최적출하가능일 비교 - 출하요청일 < 최적출하가능일 일 경우 통제기능 추가 - 2020.03.25 신성철 직장 요청.
			if(sBestDt2 > ds_list4.getcolumn(nRow,"ZZSHIP2") && Substr(ds_list4.GetColumn(nRow, "PSPID"),0,1) != 'J' && Substr(ds_list4.GetColumn(nRow, "POSID"),6,1) == 'L')
			{
				var msg = "최적출하가능일이 "+substr(sBestDt2,4,2)+"/"+substr(sBestDt2,6,2)+"일 입니다. \n이 날짜 이후로 저장해주세요.\n일정 단축 필요시 생산관리팀 연락 바랍니다.";
				gfn_openMsgWin(msg, "W", "A" );
				ds_list4.SetColumn(nRow,"ZZSHIP2",ds_list4.GetOrgColumn(nRow,"ZZSHIP2"));
				obj.fireevent = true;
				return;
			}

			if(replace(ds_list4.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list4.SetColumn(nRow,"ZZSHIP2",ds_list4.GetOrgColumn(nRow,"ZZSHIP2"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list4.getcolumn(nRow,"ZZSHIP2"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list4.SetColumn(nRow,"ZZSHIP2",ds_list4.GetOrgColumn(nRow,"ZZSHIP2"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list4.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list4.SetColumn(nRow,"ZZSHIP2",ds_list4.GetOrgColumn(nRow,"ZZSHIP2"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//
			}
			break;
		case "ZZSHIP3":
			// 최적출하가능일 비교 - 출하요청일 < 최적출하가능일 일 경우 통제기능 추가 - 2020.03.25 신성철 직장 요청.
			if(sBestDt3 > ds_list4.getcolumn(nRow,"ZZSHIP3") && Substr(ds_list4.GetColumn(nRow, "PSPID"),0,1) != 'J'&& Substr(ds_list4.GetColumn(nRow, "POSID"),6,1) == 'L')
			{
				var msg = "최적출하가능일이 "+substr(sBestDt3,4,2)+"/"+substr(sBestDt3,6,2)+"일 입니다. \n이 날짜 이후로 저장해주세요.\n일정 단축 필요시 생산관리팀 연락 바랍니다.";
				gfn_openMsgWin(msg, "W", "A" );
				ds_list4.SetColumn(nRow,"ZZSHIP3",ds_list4.GetOrgColumn(nRow,"ZZSHIP3"));
				obj.fireevent = true;
				return;
			}

			if(replace(ds_list4.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list4.SetColumn(nRow,"ZZSHIP3",ds_list4.GetOrgColumn(nRow,"ZZSHIP3"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list4.getcolumn(nRow,"ZZSHIP3"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list4.SetColumn(nRow,"ZZSHIP3",ds_list4.GetOrgColumn(nRow,"ZZSHIP3"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list4.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list4.SetColumn(nRow,"ZZSHIP3",ds_list4.GetOrgColumn(nRow,"ZZSHIP3"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			break;
		case "ZZSHIP4":
			// 최적출하가능일 비교 - 출하요청일 < 최적출하가능일 일 경우 통제기능 추가 - 2020.03.25 신성철 직장 요청.
			if(sBestDt4 > ds_list4.getcolumn(nRow,"ZZSHIP4") && Substr(ds_list4.GetColumn(nRow, "PSPID"),0,1) != 'J' && Substr(ds_list4.GetColumn(nRow, "POSID"),6,1) == 'L')
			{
				var msg = "최적출하가능일이 "+substr(sBestDt4,4,2)+"/"+substr(sBestDt4,6,2)+"일 입니다. \n이 날짜 이후로 저장해주세요.\n일정 단축 필요시 생산관리팀 연락 바랍니다.";
				gfn_openMsgWin(msg, "W", "A" );
				ds_list4.SetColumn(nRow,"ZZSHIP4",ds_list4.GetOrgColumn(nRow,"ZZSHIP4"));
				obj.fireevent = true;
				return;
			}

			if(replace(ds_list4.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list4.SetColumn(nRow,"ZZSHIP4",ds_list4.GetOrgColumn(nRow,"ZZSHIP4"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list4.getcolumn(nRow,"ZZSHIP4"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list4.SetColumn(nRow,"ZZSHIP4",ds_list4.GetOrgColumn(nRow,"ZZSHIP4"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list4.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list4.SetColumn(nRow,"ZZSHIP4",ds_list4.GetOrgColumn(nRow,"ZZSHIP4"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			break;
		case "ZZSHIP5":
			// 최적출하가능일 비교 - 출하요청일 < 최적출하가능일 일 경우 통제기능 추가 - 2020.03.25 신성철 직장 요청.
			if(sBestDt5 > ds_list4.getcolumn(nRow,"ZZSHIP5") && Substr(ds_list4.GetColumn(nRow, "PSPID"),0,1) != 'J' && Substr(ds_list4.GetColumn(nRow, "POSID"),6,1) == 'L')
			{
				var msg = "최적출하가능일이 "+substr(sBestDt5,4,2)+"/"+substr(sBestDt5,6,2)+"일 입니다. \n이 날짜 이후로 저장해주세요.\n일정 단축 필요시 생산관리팀 연락 바랍니다.";
				gfn_openMsgWin(msg, "W", "A" );
				ds_list4.SetColumn(nRow,"ZZSHIP5",ds_list4.GetOrgColumn(nRow,"ZZSHIP5"));
				obj.fireevent = true;
				return;
			}

			if(replace(ds_list4.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list4.SetColumn(nRow,"ZZSHIP5",ds_list4.GetOrgColumn(nRow,"ZZSHIP5"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list4.getcolumn(nRow,"ZZSHIP5"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list4.SetColumn(nRow,"ZZSHIP5",ds_list4.GetOrgColumn(nRow,"ZZSHIP5"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list4.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list4.SetColumn(nRow,"ZZSHIP5",ds_list4.GetOrgColumn(nRow,"ZZSHIP5"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			break;
		case "ZZSHIP6":
			/*
			// 최적출하가능일 비교 - 출하요청일 < 최적출하가능일 일 경우 통제기능 추가 - 2020.03.25 신성철 직장 요청.
			if(sBestDt6 > ds_list4.getcolumn(nRow,"ZZSHIP6") && Substr(ds_list4.GetColumn(nRow, "PSPID"),0,1) != 'J' && Substr(ds_list4.GetColumn(nRow, "POSID"),6,1) == 'L')
			{
				var msg = "최적출하가능일이 "+substr(sBestDt6,4,2)+"/"+substr(sBestDt6,6,2)+"일 입니다. \n이 날짜 이후로 저장해주세요.\n일정 단축 필요시 생산관리팀 연락 바랍니다.";
				gfn_openMsgWin(msg, "W", "A" );
				ds_list4.SetColumn(nRow,"ZZSHIP6",ds_list4.GetOrgColumn(nRow,"ZZSHIP6"));
				obj.fireevent = true;
				return;
			}*/

			if(replace(ds_list4.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list4.SetColumn(nRow,"ZZSHIP6",ds_list4.GetOrgColumn(nRow,"ZZSHIP6"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list4.getcolumn(nRow,"ZZSHIP6"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list4.SetColumn(nRow,"ZZSHIP6",ds_list4.GetOrgColumn(nRow,"ZZSHIP6"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list4.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list4.SetColumn(nRow,"ZZSHIP6",ds_list4.GetOrgColumn(nRow,"ZZSHIP6"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			break;
		case "ZZCOMP2":
			if(replace(ds_list4.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list4.SetColumn(nRow,"ZZCOMP2",ds_list4.GetOrgColumn(nRow,"ZZCOMP2"));
				var arr = [zzcomp2, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			break;
	}
	if((length(ds_list4.GetColumn(nRow,"ZZFKEYD")) > 0 && length(ds_list4.GetColumn(nRow,"ZZSTAN1")) > 0) || strColumnID == 'ZZILSIC')
	{
		ds_list4.SetColumn(nRow,"FLAG","");
		ds_list4.SetColumn(nRow,"CHK","0");
	}
	else
	{
		ds_list4.SetColumn(nRow,"FLAG","I");
		ds_list4.SetColumn(nRow,"CHK","1");
	}

	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리
********************************************************************************/
function ds_list5_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{  // alert(varNewValue);
	obj.fireevent = false;
	var sDate = replace(ds_list5.GetColumn(nRow,"SDATE"),'-','')+"(수주일자)";
	var zzship = st_zzship.Value;
	var zzcomp2 = st_zzcomp2.Value;
    var spart = substr(ds_list5.GetColumn(nRow, "POSID"),6,1);
    var sp_ause = ds_list4.GetColumn(nRow, "SP_AUSE");  // 추각자재 건 추가 

	switch( strColumnID ) {
		case "CHK":
			break;
		case "ZZACTSS":
			ds_list5.SetColumn(nRow,"ZZPMNUM","");	// 설치PM 초기화
			ds_list5.SetColumn(nRow, "ZZLIFNR", "");	// 협력업체 초기화
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");

			//if(ds_list5.GetColumn(nRow,"ZZACTSS") != '02') ds_list5.SetColumn(nRow,"ZTEAM",""); //zteam
			//if(ds_list5.GetColumn(nRow,"ZZACTSS") != '02') ds_list5.SetColumn(nRow,"ZTEAM","0"); //zteam
			fn_changeZzpmun_grd_cd(ds_list5.GetColumn(nRow,strColumnID));
			break;
		case "ZZPMNUM":
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
		case "ZZTEMNO":
  	        ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;

		case "ZZTEMNO2":
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;

		case "ZZLIFNR":
			ds_list5.SetColumn(nRow,"ZZTEMNO","");
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");

			//if(ds_list5.GetColumn(nRow,"ZZACTSS") != '02' ) ds_list5.SetColumn(nRow,"ZTEAM",""); //zteam
			//if(ds_list5.GetColumn(nRow,"ZZACTSS") != '02' ) ds_list5.SetColumn(nRow,"ZTEAM","0"); //zteam
			fn_changeZztemno_grd_cd(ds_list5.GetColumn(nRow,strColumnID));
			break;
			case "ZZLIFNR2":
			ds_list5.SetColumn(nRow,"ZZTEMNO2","");
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");

			fn_changeZztemno_grd_cd(ds_list5.GetColumn(nRow,strColumnID));
			break;
		//case "ZTEAM":
			//ds_list5.SetColumn(nRow,"FLAG","I");
			//ds_list5.SetColumn(nRow,"CHK","1");
			//break;
		case "ZZSHIP1":
			if(replace(ds_list5.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list5.SetColumn(nRow,"ZZSHIP1",ds_list5.GetOrgColumn(nRow,"ZZSHIP1"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
		    //DATASET변경시 FLAG가 'Y'인 경우 휴일체크 안함

			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list5.getcolumn(nRow,"ZZSHIP1"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list5.SetColumn(nRow,"ZZSHIP1",ds_list5.GetOrgColumn(nRow,"ZZSHIP1"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list5.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list5.SetColumn(nRow,"ZZSHIP1",ds_list5.GetOrgColumn(nRow,"ZZSHIP1"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			if(replace(ds_list5.GetOrgColumn(nRow,"ZZSHIP1"),'-','') != ds_list5.GetColumn(nRow,"ZZSHIP1"))
			{
				if(Substr(ds_list5.GetColumn(nRow, "PSPID"),0,1) == 'J')
				{
					var arg = "gubun='A'";
					gfn_openPopup("PS0201001E",true,arg);
				}
				else	// J프로젝트가 아닌 경우
				{

					Trace("varOldValue : "+replace(varOldValue,'-',''));
					Trace("ENDDAT : "+ds_gubun.GetColumn(0,"ENDDAT"));
					Trace("ZZSHIP1 : "+replace(ds_list5.GetColumn(nRow,"ZZSHIP1"),'-',''));
					//설치부만 변경사유를 입력가능
					if( ds_list5.GetColumn(nRow,"ZZFMAGA") == "X" || replace(varOldValue,'-','') <= ds_gubun.GetColumn(0,"ENDDAT") || replace(ds_list5.GetColumn(nRow,"ZZSHIP1"),'-','') <= ds_gubun.GetColumn(0,"ENDDAT") )
					{
						if(userGroup == '40')
						{
							var arg = "gubun='A'";
							gfn_openPopup("PS0201001E",true,arg);
						}
						else
						{
							var msg = st_zzship1.Value+" 변경이 ";
							gfn_showAlert("CW00114",msg);
							ds_list5.SetColumn(nRow,"ZZSHIP1",ds_list5.GetOrgColumn(nRow,"ZZSHIP1"));
							break;
						}
					}

					// 준공예정일 리드타임 추가 2020-11-30 우동훈 책임 요청
					if(ds_list5.getColumn(nRow, "ZZCHAKG") <> 'X')/*착공 미확정된 현장 대상*/
					{
						var nLTrow = ds_leadtime.SearchRow("POSID == '"+ds_list5.GetColumn(nRow, "POSID")+"'");
						if(nLTrow >= 0)
						{
							var sCalDate = gfn_getAfterDate(varNewValue,ds_leadtime.getColumn(nLTrow,"LEAD_TIME"));
							//Trace(sCalDate);
							var sDay = GetDay(sCalDate); // 6 : 토요일, 0 : 일요일
							//Trace(sDay);
							if(sDay == 6) // 토요일 + 2일
							{
								sCalDate = gfn_getAfterDate(sCalDate,2);
							}
							else if(sDay == 0) // 일요일 + 1일
							{
								sCalDate = gfn_getAfterDate(sCalDate,1);
							}

							obj.fireevent = false;
							ds_list5.SetColumn(nRow, "ZZCOMP2", sCalDate);
							obj.fireevent = true;
						}
					}

				}

			}
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
		case "ZZSHIP2":
			if(replace(ds_list5.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list5.SetColumn(nRow,"ZZSHIP2",ds_list5.GetOrgColumn(nRow,"ZZSHIP2"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list5.getcolumn(nRow,"ZZSHIP2"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list5.SetColumn(nRow,"ZZSHIP2",ds_list5.GetOrgColumn(nRow,"ZZSHIP2"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list5.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list5.SetColumn(nRow,"ZZSHIP2",ds_list5.GetOrgColumn(nRow,"ZZSHIP2"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
		case "ZZSHIP3":
			if(replace(ds_list5.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list5.SetColumn(nRow,"ZZSHIP3",ds_list5.GetOrgColumn(nRow,"ZZSHIP3"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list5.getcolumn(nRow,"ZZSHIP3"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list5.SetColumn(nRow,"ZZSHIP3",ds_list5.GetOrgColumn(nRow,"ZZSHIP3"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list5.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list5.SetColumn(nRow,"ZZSHIP3",ds_list5.GetOrgColumn(nRow,"ZZSHIP3"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
		case "ZZSHIP4":
			if(replace(ds_list5.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list5.SetColumn(nRow,"ZZSHIP4",ds_list5.GetOrgColumn(nRow,"ZZSHIP4"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list5.getcolumn(nRow,"ZZSHIP4"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list5.SetColumn(nRow,"ZZSHIP4",ds_list5.GetOrgColumn(nRow,"ZZSHIP4"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list5.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list5.SetColumn(nRow,"ZZSHIP4",ds_list5.GetOrgColumn(nRow,"ZZSHIP4"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
		case "ZZSHIP5":
			if(replace(ds_list5.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list5.SetColumn(nRow,"ZZSHIP5",ds_list5.GetOrgColumn(nRow,"ZZSHIP5"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list5.getcolumn(nRow,"ZZSHIP5"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list5.SetColumn(nRow,"ZZSHIP5",ds_list5.GetOrgColumn(nRow,"ZZSHIP5"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{

					//var spart = substr(ds_list5.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list5.SetColumn(nRow,"ZZSHIP5",ds_list5.GetOrgColumn(nRow,"ZZSHIP5"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
		case "ZZSHIP6":
			if(replace(ds_list5.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list5.SetColumn(nRow,"ZZSHIP6",ds_list5.GetOrgColumn(nRow,"ZZSHIP6"));
				var arr = [zzship, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list5.getcolumn(nRow,"ZZSHIP6"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list5.SetColumn(nRow,"ZZSHIP6",ds_list5.GetOrgColumn(nRow,"ZZSHIP6"));
					obj.fireevent = true;
					return;
				}

				//======workday 17일 이후로만 입력가능(특정 사용자 예외:신성철)===============//
				if(fn_WorkDay_17(spart,sp_ause) == false)
				{


					//var spart = substr(ds_list5.GetColCount(0,"POSID"),7,1);
					gfn_showAlert("CW00238");
					ds_list5.SetColumn(nRow,"ZZSHIP6",ds_list5.GetOrgColumn(nRow,"ZZSHIP6"));
					obj.fireevent = true;
					return;
				}
				//===========================================================================//

			}
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
		case "ZZFKEYD":

			if (varNewValue == '0' && (trim(ds_list5.GetColumn(nRow,"ZZCHAKG")) == 'X' || trim(ds_list5.GetColumn(nRow,"ZZSTAN1")) == '1' || trim(ds_list5.GetColumn(nRow,"ZZJUNGO")) == 'X'))
			{
				ds_list5.SetColumn(nRow,"ZZFKEYD","1");
			}
			else if(varNewValue == '1')
			{
				ds_list5.SetColumn(nRow,"FLAG","I");
				ds_list5.SetColumn(nRow,"CHK","1");
			}
			else
			{
				ds_list5.SetColumn(nRow,"FLAG","I");
				ds_list5.SetColumn(nRow,"CHK","1");
			}
			break;
		case "ZZSTAN1":

			if (varNewValue == '1' && (trim(ds_list5.GetColumn(nRow,"ZZFKEYD")) != '1' || trim(ds_list5.GetColumn(nRow,"ZZCHAKG")) != 'X'))
			{
				ds_list5.SetColumn(nRow,"ZZSTAN1","0");
			}
			else if (varNewValue == '0' && (trim(ds_list5.GetColumn(nRow,"ZZJUNGO")) == 'X'))
			{
				ds_list5.SetColumn(nRow,"ZZSTAN1","1");
			}
			else if(varNewValue == '1')
			{
				ds_list5.SetColumn(nRow,"FLAG","I");
				ds_list5.SetColumn(nRow,"CHK","1");
			}
			else
			{
				ds_list5.SetColumn(nRow,"FLAG","I");
				ds_list5.SetColumn(nRow,"CHK","1");
			}
			break;
		case "ZZCOMP2":
		    // 체크로직 제거 2019.07.31 신성철 직장 요청.
			/* [준공예정일]은 수주일자보다 같거나 이후 일자로 입력하여 주십시오.
			if(replace(ds_list5.GetColumn(nRow,"SDATE"),'-','') > replace(varNewValue,'-',''))
			{
				ds_list5.SetColumn(nRow,"ZZCOMP2",ds_list5.GetOrgColumn(nRow,"ZZCOMP2"));
				var arr = [zzcomp2, sDate ];
				gfn_showAlert("CW00007", arr);
				break;
			}
			*/
/*
			if(	N_COPY != "Y")
			{
				ds_cond_day.SetColumn(0,"I_CHECK_DATE",ds_list5.getcolumn(nRow,"ZZCOMP2"));
				fn_checkWorkDay();
				if(E_TYPE != 0)
				{
					gfn_showAlert("CW00067");
					ds_list5.SetColumn(nRow,"ZZCOMP2",ds_list5.GetOrgColumn(nRow,"ZZCOMP2"));
					obj.fireevent = true;
					return;
				}
			}
*/
			if(replace(ds_list5.GetOrgColumn(nRow,"ZZCOMP2"),'-','') != ds_list5.GetColumn(nRow,"ZZCOMP2"))
			{
				//설치부,지방사무소 변경사유를 입력가능(ZZSTAN1='준공F', ZZSTAN2='준공최초마감')
				if( ds_list5.GetColumn(nRow,"ZZSTAN2") == "X" || replace(ds_list5.GetColumn(nRow,"ZZCOMP2"),'-','') <= ds_gubun.GetColumn(1,"ENDDAT") )
				{
					if(userGroup == '40')
					{
						var arg = "gubun='B'";
						gfn_openPopup("PS0201001E",true,arg);
					}
					else if(( userGroup == '10' || userGroup == '30' ) && ds_list5.GetColumn(nRow,"ZZSTAN1") != "X")
					{
						var arg = "gubun='B'";
						gfn_openPopup("PS0201001E",true,arg);
					}
					else
					{
						var msg = st_zzcomp2.Value+" 변경이 ";
						gfn_showAlert("CW00114",msg);
						ds_list5.SetColumn(nRow,"ZZCOMP2",ds_list5.GetOrgColumn(nRow,"ZZCOMP2"));
						break;
					}
				}
			}
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
		case "ZZILSIC" :
			break;
		default	:
			ds_list5.SetColumn(nRow,"FLAG","I");
			ds_list5.SetColumn(nRow,"CHK","1");
			break;
	}

	obj.fireevent = true;
}
// 달력 처리 부분
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1] + 100;
	if (cal_temp.top > 400)
	{
		cal_temp.top = 400;
	}
	cal_temp.Left = rect[0] + 20;
	if (cal_temp.Left > 790)
	{
		cal_temp.Left = 790;
	}
	cal_temp.Visible = true;

	if(nCell == 8)
		cal_temp.value = ds_list4.getColumn(nRow, "ZZSHIP1");
	else if(nCell == 10)
		cal_temp.value = ds_list4.getColumn(nRow, "ZZSHIP2");
	else if(nCell == 11)
		cal_temp.value = ds_list4.getColumn(nRow, "ZZSHIP3");
	else if(nCell == 12)
		cal_temp.value = ds_list4.getColumn(nRow, "ZZSHIP4");
	else if(nCell == 13)
		cal_temp.value = ds_list4.getColumn(nRow, "ZZSHIP5");
	else if(nCell == 14)
		cal_temp.value = ds_list4.getColumn(nRow, "ZZSHIP6");
	else if(nCell == 15)
		cal_temp.value = ds_list4.getColumn(nRow, "ZZCOMP2");
	cal_temp.SetFocus();
}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	if(substr(strText,0,6) < substr(gfn_getCurrDate(),0,6))
	{
		var arg = "";
		if(Tab00.tab3.grd_list.GetClickedCell() == 8 || Tab00.tab3.grd_list.GetClickedCell() == 10 || Tab00.tab3.grd_list.GetClickedCell() == 11 ||
		   Tab00.tab3.grd_list.GetClickedCell() == 12 || Tab00.tab3.grd_list.GetClickedCell() == 13 || Tab00.tab3.grd_list.GetClickedCell() == 14)
		   arg = st_zzship.value;
		else if(Tab00.tab3.grd_list.GetClickedCell() == 15)
		   arg = st_zzcomp2.value;   

		var arr = [arg, st_currDate.Value ];
		gfn_showMsg("CW00007", arr);
		return;
	}

	if(Tab00.tab3.grd_list.GetClickedCell() == 8)
		ds_list4.SetColumn(ds_list4.row, "ZZSHIP1", strText);
	else if(Tab00.tab3.grd_list.GetClickedCell() == 10)
		ds_list4.SetColumn(ds_list4.row, "ZZSHIP2", strText);
	else if(Tab00.tab3.grd_list.GetClickedCell() == 11)
		ds_list4.SetColumn(ds_list4.row, "ZZSHIP3", strText);
	else if(Tab00.tab3.grd_list.GetClickedCell() == 12)
		ds_list4.SetColumn(ds_list4.row, "ZZSHIP4", strText);
	else if(Tab00.tab3.grd_list.GetClickedCell() == 13)
		ds_list4.SetColumn(ds_list4.row, "ZZSHIP5", strText);
	else if(Tab00.tab3.grd_list.GetClickedCell() == 14)
		ds_list4.SetColumn(ds_list4.row, "ZZSHIP6", strText);
	else if(Tab00.tab3.grd_list.GetClickedCell() == 15)
		ds_list4.SetColumn(ds_list4.row, "ZZCOMP2", strText);

	obj.Visible = false;
	global.FRM_BOTTOM.st_msg.value = "";
	global.FRM_BOTTOM.img_msgType.imageId = "";
}
