/********************************************************************************
* 기      능   :  그리드에서 콤보 변경시 다음콤보값 변경(사무소)
********************************************************************************/
function fn_changeZzlifnr30(currow)
{
	return "ZZPMNUM=" + quote(ds_list4.GetColumn(currow,"ZZPMNUM"));
}
function fn_changeZztemno30(currow)
{
	return "ZZLIFNR=" + quote(ds_list4.GetColumn(currow,"ZZLIFNR"));
}
function fn_changeZzlifnr40(currow)
{
	return "ZZPMNUM=" + quote(ds_list4.GetColumn(currow,"ZZPMNUM"));
}
/********************************************************************************
* 기      능   :  그리드에서 콤보 변경시 다음콤보값 변경(설치부)
********************************************************************************/
function fn_changeZzpmun_grd_cd(actss)
{
	ds_zzpmnum_grd_cd.filter("ZZACTSS == " + quote( actss) );
}
function fn_changeZztemno_grd_cd(lifnr)
{
	ds_zztemno_grd_cd.filter("ZZLIFNR == " + quote( lifnr) );
}

function fn_changeZzpmnum1(currow)
{
	return "ZZACTSS=" + quote(ds_list4.GetColumn(currow,"ZZACTSS"));
}
