function Tab00_tab4_grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	N_CELL = nCell;
	N_ROW = nRow;


	// 복사버튼 클릭 시
	if(nCell == 9)
	{
		// 원래 입력불가 상태일 경우에는 
		if(!fn_changeTf2(nRow,ds_list5.getColumn(nRow,"AUFNR2"),ds_list5.getColumn(nRow,"ZZFKEYD"),ds_list5.getColumn(nRow,"ZZSTAN1")))
		{
			return;
		}

		var sZzship1 = ds_list5.GetColumn(nRow, "ZZSHIP1");
		if(sZzship1 == '' || sZzship1 == '00000000')
		{
			gfn_openMsgWin("기계실 출하요청일을 입력하세요.");
			return;
		}

		ds_list5.SetColumn(nRow, "ZZSHIP2", sZzship1);
		ds_list5.SetColumn(nRow, "ZZSHIP3", sZzship1);
		ds_list5.SetColumn(nRow, "ZZSHIP4", sZzship1);
		ds_list5.SetColumn(nRow, "ZZSHIP5", sZzship1);
	}

}

function Tab00_tab3_grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	N_CELL = nCell;
	N_ROW = nRow;

	// 복사버튼 클릭 시
	if(nCell == 9)
	{
		var sZzship1 = ds_list4.GetColumn(nRow, "ZZSHIP1");
		if(sZzship1 == '' || sZzship1 == '00000000')
		{
			gfn_openMsgWin("기계실 출하요청일을 입력하세요.");
			return;
		}

		if(length(ds_list4.Getcolumn(nRow,"AUFNR2")) > 0)
		{
			ds_list4.SetColumn(nRow, "ZZSHIP2", sZzship1);
		}
		if(length(ds_list4.Getcolumn(nRow,"AUFNR3")) > 0)
		{
			ds_list4.SetColumn(nRow, "ZZSHIP3", sZzship1);
		}
		if(length(ds_list4.Getcolumn(nRow,"AUFNR4")) > 0)
		{
			ds_list4.SetColumn(nRow, "ZZSHIP4", sZzship1);
		}
		if(length(ds_list4.Getcolumn(nRow,"AUFNR5")) > 0)
		{
			ds_list4.SetColumn(nRow, "ZZSHIP5", sZzship1);
		}




	}
}

/********************************************************************************
* 기      능   : 그리드의 특정로우 데이타를 해당로우에 복사
********************************************************************************/
function btn_copy_OnClick(obj)
{
	N_COPY = "Y"; //DATASET변경시 FLAG가 'Y'인 경우 휴일체크 안함

	if(Tab00.TabIndex == 2) {
		var arg = "tGrid=Tab00.tab3.grd_list";
		gfn_openPopup("PS9602002A", true, arg);
	} else if(Tab00.TabIndex == 3) {
		var arg = "tGrid=Tab00.tab4.grd_list";
		gfn_openPopup("PS9602002A", true, arg);
	} else {
		return;
	}

	N_COPY = "N";
}

