function ufn_disable(currow,check)
{
	//TXT04값이 E(정산),F(완료),C(취소) 인 경우 수정 불가 -20090827(방인호D 요청)
	if(check == 'X' || ds_list5.GetColumn(currow,"TXT04") == 'E' || ds_list5.GetColumn(currow,"TXT04") == 'F' || ds_list5.GetColumn(currow,"TXT04") == 'C') return "none";
	//if(check == 'X') return "none";
	else return "combo";
}
