function Tab00_tab4_grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(userGroup != '40') return;

	var arg = "";

	/*
	if(nCell == 8 && length(trim(ds_list5.GetColumn(nRow,"ZZCHAKG"))) == 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP1'";
	}
	*/
	if(nCell == 8 && length(trim(ds_list5.GetColumn(nRow,"AUFNR1"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP1'";
	}

	else if(nCell == 10 && length(trim(ds_list5.GetColumn(nRow,"AUFNR2"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP2'";
	}
	else if(nCell == 11 && length(trim(ds_list5.GetColumn(nRow,"AUFNR3"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP3'";
	}
	else if(nCell == 12 && length(trim(ds_list5.GetColumn(nRow,"AUFNR4"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 ) 
	{
		arg = "date_='ZZSHIP4'";
	}	
	else if(nCell == 13 && length(trim(ds_list5.GetColumn(nRow,"AUFNR5"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP5'";
	}
	else if(nCell == 14 && length(trim(ds_list5.GetColumn(nRow,"AUFNR6"))) > 0)
	{
		arg = "date_='ZZSHIP6'";
	}
	else if(nCell == 17 && length(trim(ds_list5.GetColumn(nRow,"(ZZSTAN1)"))) == 0)
	{
		arg = "date_='ZZCOMP2'";
	}
	else
	{
		return;
	}
	gfn_openPopup("PS9602003A", true, arg);

}

/********************************************************************************
* 기      능   : 사업부 선택시 ds_lifnr 데이터셋 값 filtering
********************************************************************************/
function chk_so2_OnClick(obj,strValue)
{
	var so = ' ';
	var so2 = ' ';
	var so3 = ' ';
	var so4 = ' ';
	var so5 = ' ';
	var so7 = ' ';

	if(chk_so.Value == '1')  so =  'X';
	if(chk_so2.Value == '1') so2 = 'X';
	if(chk_so3.Value == '1') so3 = 'X';
	if(chk_so4.Value == '1') so4 = 'X';
	if(chk_so5.Value == '1') so5 = 'X';
	if(chk_so7.Value == '1') so7 = 'X';


	if(chk_so.Value+chk_so2.Value+chk_so3.Value+chk_so4.Value+chk_so5.Value+chk_so7.Value == '000000')
	{
		ds_zzlifnr_cd.Filter("SO5=='' || CODE==''");
	}	
	else
	{
	    ds_zzlifnr_cd.Filter("CODE=='' || SO=="+quote(so) +" || SO2=="+quote(so2) +" || SO3=="+quote(so3) +" || SO4=="+quote(so4) +" || SO5=="+quote(so5) +" || SO7=="+quote(so7));
	}
	//cb_combo3.Index = 0;	
}



function btn_gis_OnClick(obj) {

		var arg = "pjt=" + quote(ds_list.GetColumn(ds_list.currow, "POSID")) + 
					" hno=0" + 
					" user_id=" + quote(G_USER_ID);

		gfn_openPopup("CS9608001A", true, arg);
}



