/********************************************************************************
* 기      능   : 첨부파일 열기 
********************************************************************************/
function btn_openFile_OnClick(obj)
{
	fileDialog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length(fileDialog.FileName ) == 0 ) {
		return;
	}

	var fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "10 Mbytes" );	
		return;
	}

	/*
	//한글제목 입력금지(한글제목깨짐)
	for( var i = 0; i < length( fileDialog.FileName); i++ ) {
		if ( lengthb(charAt( fileDialog.FileName, i)) == 2 ) {
			gfn_showMsg("CW00213");
			return;
		}
	}
	*/

	// 그리드 저장 대상 로우
	if(ds_list3.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}
//alert(fileDialog.FileName);
	Tab00.tab2.ed_FileName.value = fileDialog.FileName;


		if(ds_list3.GetColumn(0, "CHECK")==1)
		{

			// 파일정보
			//if(length(ed_FileName.value) > 0 )
			//{
				ds_list3.setColumn(0, "FNAME", fileDialog.FileName);
				ds_list3.setColumn(0, "FSIZE", fileSize);
				ds_list3.setColumn(0, "FPATH", fileDialog.FilePath);

				var lv_fn = substr( fileDialog.FileName, length(fileDialog.FileName) - 4, 4);
			    ds_list3.SetColumn(0, "FNAME_TMP", lv_fn);
				//ds_list.setColumn(i, "GBN", cb_gbn.Value);
			//} 

		}


}

/********************************************************************************
* 기      능   : 현장답사이력 그리드 셀 더블클릭 이벤트
********************************************************************************/
function Tab00_tab2_Grid0_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	//첨부파일(보기) 더블클릭시
  if ( nCell == 10 ){
		// 해당하는 ROW의 첨부파일 데이터 가져오기
		// 첨부파일 SELECT 해오기

		if(ds_list3.GetColumn(ds_list3.rowposition, "INPT") != "A") return;
		if(ToNumber(ds_list3.GetColumn(ds_list3.rowposition, "FCNT")) <= 0) return;

		ds_condFile.clearData();
		ds_condFile.addRow();
		ds_condFile.SetColumn(0, "PSPID", ds_list3.GetColumn(ds_list3.rowposition, "PSPID"));
		ds_condFile.SetColumn(0, "SEQNO", gfn_fillZero(ds_list3.GetColumn(ds_list3.rowposition, "SEQNO"), 3) );

		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001B_S13");	// 첨부파일 정보
		tit_callService(
		 ""
		, ""
		, "ds_condFile=ds_condFile"
		, "ds_file=ds_file"
		, ""
		, "fn_selectFile"
		, ""
		, ""
		, true);


	}
	else
	{
		 var sInpt = ds_list3.GetColumn(nRow, "INPT");

		 if(sInpt == "A")	// 현장답사 일 경우에만 조회가능
		 {
			 var formID="";
			 var gbn = "C"; // 수정 건

			 formID = "PS0201001G";  
			//break;
			 var pjt = ds_list3.GetColumn(nRow, "PSPID");
			 var seq = ds_list3.GetColumn(nRow, "SEQNO");


			 var arg = "PRE_PJT="+quote(pjt);
				 arg += " PRE_GBN="+quote(gbn);
				 arg += " PRE_SEQ="+quote(seq);

			//SELFLG = "N";
			 gfn_openPopup(formID, true, arg);
		 }

	}

}			
/********************************************************************************
* 기      능   : 저장전에 자료 검증 
********************************************************************************/
function fn_checkSave()
{
//alert("yyy");
		ds_cond3.ClearData();
		ds_cond3.AddRow();
	//	ds_cond3.setColumn(0, "GBN", ds_list_tmp.GetColumn(i, "GBN"));
		ds_cond3.setColumn(0, "PSPID", ds_list3.GetColumn(0, "PSPID"));
		ds_cond3.setColumn(0, "SEQNO", ds_list3.GetColumn(0, "SEQNO"));
		ds_cond3.setColumn(0, "FNAME", ds_list3.GetColumn(0, "FNAME"));

		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S12");		   
        tit_addSearchActionInfo("ps02:PS0201001B_S7");	
		tit_callService(
			""
			, ""
			, "ds_cond3=ds_cond3"
			, "ds_list8=ds_list8 ds_list7=ds_list7"
			, ""
			, ""
			, ""
			, ""
			, true);

		if( ds_list8.GetColumn(0, "CNT") > 0 )
		{
			global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
			global.FRM_BOTTOM.st_msg.color = "yellow";       
			global.FRM_BOTTOM.st_msg.value = "같은 호기에 동일한 파일이 존재합니다.";
			return false;
		}



	return true;
}

