	formID = "PS0201001G";  

	var pjt = ds_cond.GetColumn(0, "PSPID");


	if(Length(Trim(pjt)) < 6)
	{
		gfn_openMsgWin("조회조건에서 프로젝트 번호를 확인해주세요.");
		return;
