     gfn_showAlert("CW10025",'프로젝트 정보 탭에서 조회 버튼을 클릭해야 현장답사 이력을 관리할 수 있습니다');
    return;	
   }
}
/********************************************************************************
* 기      능   : 수정버튼 CLICK
********************************************************************************/
function Tab00_tab2_btn_mod_OnClick(obj)
{
  ds_dchk.ClearData();	
  	// 대상 항목 체크
   if(ds_list3.FindRow("CHECK", 1)==-1){ 
		gfn_showMsg("CW00046");  
		return;
	}

    ds_list3.Filter("CHECK==1");
	ds_dchk.copyF(ds_list3);
	ds_list3.unFilter();

	if(ds_dchk.rowcount > 1 )
	{
	   global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
       global.FRM_BOTTOM.st_msg.color = "yellow";       
       global.FRM_BOTTOM.st_msg.value = "하나의 정보만 선택해주세요.";
	    return;  
	}
	else
	{


     for(var i=0; i<ds_list3.GetRowCount(); i++){

	 	if(ds_list3.GetColumn(i, "CHECK")==1){
        //alert("a");
         var formID="";
       	 var gbn = "C"; // 수정 건

      	 formID = "PS0201001G";  
	//break;
	     var pjt = ds_list3.GetColumn(i, "PSPID");
	     var seq = ds_list3.GetColumn(i, "SEQNO");

  	     var arg = "PRE_PJT="+quote(pjt);
	         arg += " PRE_GBN="+quote(gbn);
             arg += " PRE_SEQ="+quote(seq);

	//SELFLG = "N";
	gfn_openPopup(formID, true, arg);

    }

    }
  }

}
/********************************************************************************
* 기      능   : 삭제 버튼 CLICK
********************************************************************************/
function Tab00_tab2_btn_del_OnClick(obj)
{
	// 체크된 ROW가 없을 경우 리턴
	if(ds_list3.FindRow("CHECK", '1')==-1){ 
		gfn_showMsg("CW00046");  
		return;
	}

	// SEQNO에 FillZero 세팅.
	for(var i=0; i < ds_list3.rowcount ; i++)
	{
		var sChk = ds_list3.GetColumn(i, "CHECK");

		if(sChk == "1")
		{
			ds_list3.SetColumn(i, "SEQNO", gfn_fillZero(ds_list3.getColumn(i, "SEQNO"), 3));
		}
	}

	// 현장답사록 화면에서 자동기록으로 등록된 경우만 삭제가능 로직 추가 2019-02-15 송광용 (고범석 GJ 요청)


	// # 현장답사록인 경우 파일 및 데이터 삭제
	ds_condDel.ClearData();
	ds_condDel.addRow();

	var sSeqList = "";

	// SEQ_LIST 만들기(복수개의 seqno에 대한 파일서버 이미지 삭제에 처리) -- ('016', '017')
	for(var i=0 ; i < ds_list3.rowcount ; i++)
	{
		var sChk   = ds_list3.getColumn(i, "CHECK");

		var sPspid = ds_list3.getColumn(i, "PSPID");

		if(sChk == "1")
		{
			ds_condDel.setColumn(0, "PSPID", sPspid);
			sSeqList += "'"+gfn_fillZero(ds_list3.getColumn(i, "SEQNO"), 3)+"',";
		}
	}

	sSeqList = "('"+SubStr(sSeqList,1,Length(sSeqList)-2)+")";
	ds_condDel.SetColumn(0, "SEQNO_LIST", sSeqList);

	qry= " AND SEQNO IN "+sSeqList;

	args = " SQRY="+quote(qry);	


	ds_list3.Filter("CHECK==1 && INPT=='A'");
	ds_del.copyF(ds_list3);
	ds_list3.unFilter();

	if(ds_del.GetRowCount() > 0)
	{
		//파일 서버에서 해당 파일 목록 가져오기
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001B_S14");	// 첨부파일 정보

		tit_callService(
		 ""
		, ""
		, "ds_cond=ds_condDel"
		, "ds_delFile=ds_file"
		, args
		, ""
		, ""
		, ""
		, true);

		// 파일서버에서 파일삭제
		for(var i=0; i<ds_delFile.rowcount; i++)
		{
			var sFileName = ds_delFile.GetColumn(i, "FNAME");

			var arr = tit_fileDelete("PS",ds_delFile.getColumn(i, "FNAME_APP"), false);
		}

		// 현장답사 데이터 삭제처리
		tit_clearActionInfo();
		tit_addMultiActionInfo("ps02:PS0201001B_D1");	//ZPST0019
		tit_addMultiActionInfo("ps02:PS0201001B_D3");	//첨부파일 테이블 ZPST0019F
		tit_addMultiActionInfo("ps02:PS0201001B_D2");	//ZPSTPM01

		tit_callService(
			  ""
			, ""
			, "ds_del=ds_del"
			, ""
			, ""
			, "" 
			, ""
			, ""
			, true);
	}



	// # 자동기록의 경우
	ds_del.clearData();
	ds_list3.Filter("CHECK==1 && INPT=='D' && AUTO_DEL=='Y'");
	ds_del.copyF(ds_list3);
	ds_list3.unFilter();

	if(ds_del.GetRowCount() > 0)
	{
		// 자동기록 데이터 삭제 처리
		tit_clearActionInfo();
		tit_addMultiActionInfo("ps02:PS0201001B_D2");	//ZPSTPM01

		tit_callService(
			  ""
			, ""
			, "ds_del=ds_del"
			, ""
			, ""
			, "" 
			, ""
			, ""
			, true);
	}


	// 재조회
	fn_query2();
}


