/********************************************************************************
* 기      능   : 현장 여건 준비 상태 저장 버튼
********************************************************************************/
function Tab00_tab2_btn_subSave_OnClick(obj)
{
	if(Length(Trim(ed_pspid.Text)) != 6)
	{
		gfn_openMsgWin("프로젝트 번호 조회 후, 저장 해주세요.");
		return;
	}

	var sChk1 = ds_chkStus.getColumn(0, "CHK1");
	var sChk2 = ds_chkStus.getColumn(0, "CHK2");
	var sChk3 = ds_chkStus.getColumn(0, "CHK3");
	var sChk4 = ds_chkStus.getColumn(0, "CHK4");
	var sChk5 = ds_chkStus.getColumn(0, "CHK5");
	// 이현용 책임 요청으로 새로 추가 20210824
	var sChk6 = ds_chkStus.getColumn(0, "CHK6");
	var sChk7 = ds_chkStus.getColumn(0, "CHK7");
	var sChk8 = ds_chkStus.getColumn(0, "CHK8");
	var sChk9 = ds_chkStus.getColumn(0, "CHK9");

	if(sChk1 == "1" && sChk2 == "1" && sChk3 == "1" && sChk4 == "1" && sChk5 == "1" && sChk6 == "1" && sChk7 == "1" && sChk8 == "1" && sChk9 == "1")	// 모두 체크시 COMPLT = 완료
	{
		 ds_chkStus.SetColumn(0, "COMPLT", "B");
	}
	else	// COMPLT = 미완료
	{
		ds_chkStus.SetColumn(0, "COMPLT", "A");
	}

    tit_clearActionInfo();
    tit_addSelectExActionInfo("ps02:PS0201001B_S12", "ps02:PS0201001B_I2", "ps02:PS0201001B_U6");
	//tit_addMultiActionInfo("ps02:PS0201001B_I");	//ZPST0019
	//tit_addMultiActionInfo("ps02:PS0201001B_I");	//ZPST0019

	tit_callService(
	  ""
	, ""
	, "ds_chkStus=ds_chkStus"
	, ""
	, ""
	, "fn_afterSave" 
	, ""
	, ""
	, true);

}

/********************************************************************************
* 기      능   : 저장 완료 후 callback
********************************************************************************/
function fn_afterSave(errCode, errMsg)
{
	if ( errCode!= 0 ) 
	{		
		gfn_showMsg("CE00001");		
	} 
	else 
	{
		// 메모가 있을 경우, ZPST0019에 자동기록 추가
		if(ds_chkStus.rowcount > 0)
		{
			var sNowBigo = ds_chkStus.GetColumn(0, "BIGO");
			var sPreBigo = ds_chkStus.GetColumn(0, "ORG_BIGO");

			if(sNowBigo != "" && length(trim(sNowBigo)) != 0 && sNowBigo != sPreBigo)	// 메모가 비어있지 않고, 변경되었을 경우
			{
				ds_chkStus.SetColumn(0, "AUTO_DEL", "Y");

				// 자동기록 저장(ZPST0019)
				tit_clearActionInfo();	
				tit_addSingleActionInfo("ps02:PS0201001B_I3");
				tit_callService(
					""
					, ""        
					, "ds_chkStus=ds_chkStus"
					, ""
					, ""
					, ""
					, ""
					, ""
					,true  // ""
					);
			}
		}

		gfn_showMsg("CI00003");

		// 재조회
		fn_query2();
	}	
}

/********************************************************************************
* 기      능   : 첨부파일 조회 후 callback
********************************************************************************/
function fn_selectFile(errCode, errMsg)
{


	if(ds_file.rowcount == 0) return;

	//# 파일이 1개이고, 그 파일이 PDF일 경우에는 파일 오픈
	if(ds_file.rowcount == 1 && (right(ds_file.GetColumn(0, "FNAME"),3) == "pdf" || right(ds_file.GetColumn(0, "FNAME"),3) == "PDF"))
	{
		if( length(ds_file.getColumn(0, "FNAME")) > 0) 
		{
				// 첨부 파일 처리 
			   fn_createWait("", this);

				var FilePath = "c:\\Temp" ;		
				var FileExec = "c:\\Temp\\" + ds_file.getColumn(0, "FNAME");

				saveDialog.FileName = ds_file.getColumn(0, "FNAME");//tit_getLocalFileName(ds_list2.getColumn(nRow, "FNAME"));

				var rMsg = tit_fileRead(
											httpFile
											, "PS"  	  // 저장 디렉토리
											, ds_file.getColumn(0, "FNAME_APP")	// 서버에서 전송받을 이름
											, FilePath    // 로컬 경로
											, ds_file.getColumn(0, "FNAME")    // 로컬 파일명
										   );

				fn_destroyWait();
				if ( rMsg[0] == "E" ) {
					if ( length(trim(rMsg[1])) > 0 ) {
						 //  ExecShell(FileExec);
						gfn_showAlert("CW10025", rMsg[1]);
					} else {
						gfn_showAlert("CW10025", "파일 다운로드에 실패하였습니다.");
					}
				} else {	
						ExecShell(FileExec);		
				}	


		}
	}
	else	// 그 외는 이미지뷰어 팝업
	{
		var formID="";
		formID = "PS0201001H";  
		//break;
		var sCdate   = ds_list3.GetColumn(ds_list3.rowposition, "CDATE");
		var sGbn     = ds_list3.GetColumn(ds_list3.rowposition, "GBN");
		var sCname   = ds_list3.GetColumn(ds_list3.rowposition, "CNAME");
		var sCnt     = ds_list3.GetColumn(ds_list3.rowposition, "FCNT");
		var sConsult = ds_list3.GetColumn(ds_list3.rowposition, "CONSULT");
		var sPspid   = ds_list3.GetColumn(ds_list3.rowposition, "PSPID");
		var sSeqNo   = gfn_fillZero(ds_list3.GetColumn(ds_list3.rowposition, "SEQNO"), 3);

		var arg = "PRE_CDATE="+quote(sCdate);
			arg += " PRE_GBN="+quote(sGbn);
			arg += " PRE_CNAME="+quote(sCname);
			arg += " PRE_FCNT="+quote(sCnt);
			arg += " PRE_CONSULT="+quote(sConsult);
			arg += " PRE_PSPID="+quote(sPspid);
			arg += " PRE_SEQNO="+quote(sSeqNo);


		gfn_openPopup(formID, false, arg);

		// ds_list3 재조회
		fn_query2();
	}

}

function fn_afterAct() {}

/*****************************************************************************************************
* 기      능   : Grid 협력업체 콤보 필터 (선택된 사무소 우선, SORT 번호로 정렬) - 2019.03.04 정태성GJ 요청
******************************************************************************************************/
function ufn_lifnrFilter(gbn,nRow)
{
	var so = ' ';
	var so2 = ' ';
	var so3 = ' ';
	var so4 = ' ';
	var so5 = ' ';
	var so7 = ' ';
	var str = "";
	var sZzactss = "";

	if(gbn == 'I')
	{
		sZzactss = ds_list4.GetColumn(nRow, "ZZACTSS");
	}
	else
	{
		sZzactss = ds_list5.GetColumn(nRow, "ZZACTSS");
	}

	ds_tempLifnr.ClearData();
	ds_zzlifnr_grd_cd.UnFilter();

	if(chk_so.Value == '1')  so =  'X';
	if(chk_so2.Value == '1') so2 = 'X';
	if(chk_so3.Value == '1') so3 = 'X';
	if(chk_so4.Value == '1') so4 = 'X';
	if(chk_so5.Value == '1') so5 = 'X';
	if(chk_so7.Value == '1') so7 = 'X';


	if(chk_so.Value+chk_so2.Value+chk_so3.Value+chk_so4.Value+chk_so5.Value+chk_so7.Value == '000000')
	{

		ds_zzlifnr_grd_cd.Filter("SO5=='' || ZZLIFNR==''");
		ds_tempLifnr.Copy(ds_zzlifnr_grd_cd);

		for(var i=0;i<ds_tempLifnr.rowcount;i++)
		{
			if(ds_tempLifnr.GetColumn(i, "ZZACTSS") == sZzactss)	// 앞에서 선택된 사무소와 같은 경우
			{
				ds_tempLifnr.SetColumn(i, "ZSORT", 1);
			}
			else
			{
				ds_tempLifnr.SetColumn(i, "ZSORT", 2);
			}
		}


		ds_zzlifnr_grd_cd.Copy(ds_tempLifnr);
		ds_zzlifnr_grd_cd.Sort("ZSORT:A,SORT:A");
		//Trace(ds_zzlifnr_grd_cd.saveXML());
		str = "ZZLIFNR=='' || SO5==''";
	}
	else
	{	
	    ds_zzlifnr_grd_cd.Filter("ZZLIFNR=='' || SO=="+quote(so) +" || SO2=="+quote(so2) +" || SO3=="+quote(so3) +" || SO4=="+quote(so4) +" || SO5=="+quote(so5) +" || SO7=="+quote(so7));
	    ds_tempLifnr.Copy(ds_zzlifnr_grd_cd);
	    for(var i=0;i<ds_tempLifnr.rowcount;i++)
		{
			if(ds_tempLifnr.GetColumn(i, "ZZACTSS") == sZzactss)	// 앞에서 선택된 사무소와 같은 경우
			{
				ds_tempLifnr.SetColumn(i, "ZSORT", 1);
			}
			else
			{
				ds_tempLifnr.SetColumn(i, "ZSORT", 2);
			}
		}


		ds_zzlifnr_grd_cd.Copy(ds_tempLifnr);
	    ds_zzlifnr_grd_cd.Sort("ZSORT:A,SORT:A");

	    //Trace(ds_zzlifnr_grd_cd.saveXML());

	    str = "ZZLIFNR=='' || SO==" + quote(so) + "|| SO2==" +quote(so2)+ "|| SO3==" +quote(so3)+ "|| SO4==" +quote(so4)+ "|| SO5==" +quote(so5)+ "|| SO7==" +quote(so7);
	}

	//Trace(str);
	return;
}

