function ds_list3_OnRowPosChanged(obj,nOldRow,nRow)
{

	if(ds_list3.GetColumn(nRow, "CID") == G_USER_ID)
	{
		Tab00.tab2.btn_del.Enable = true;
	}
	else
	{
		Tab00.tab2.btn_del.Enable = false;
	}
}


function ed_pspid_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13)
	{
		if(length(ed_pspid.Text) > 0)
		{
			fn_query();
		}

	}
}

/* 자동기록보기 추가 - 2019-12-30 고범석 gjn요청  */
function Tab00_tab2_chk_auto_OnClick(obj,strValue)
{
	ds_list3.UnFilter();

	if(strValue == '1')
	{
		ds_list3.Filter("");
	}
	else
	{
		ds_list3.Filter("INPT == 'A'");
	}
}

// 착공 입력 시, 변경버튼 클릭
function Tab00_tab3_btn_insChg_OnClick(obj)
{
	var resultCnt = 0;
	for(var i=0; i<ds_list4.rowcount; i++)
	{

		// 변경사항이 없으면.
		if(ds_list4.GetColumn(i, "ZZPMNUM") != ds_list4.GetOrgColumn(i, "ZZPMNUM") ||
			ds_list4.GetColumn(i, "ZZLIFNR") != ds_list4.GetOrgColumn(i, "ZZLIFNR") ||
			ds_list4.GetColumn(i, "ZZTEMNO") != ds_list4.GetOrgColumn(i, "ZZTEMNO") ||
			ds_list4.GetColumn(i, "ZZCOMP2") != ds_list4.GetOrgColumn(i, "ZZCOMP2")
		  )
		{
			resultCnt++;	
		}

	}

	if(resultCnt == 0)
	{
		gfn_openMsgWin("변경사항이 존재하지 않습니다.");
		return;
	}

	ds_list4_1.ClearData();


	// 저장 처리

	var rows = 0;
	for(var j=0; j<ds_list4.rowcount; j++)
	{

		if(ds_list4.getcolumn(j,"FLAG") == "I" && (ds_list4.GetColumn(j, "ZZPMNUM") != ds_list4.GetOrgColumn(j, "ZZPMNUM") || 
													ds_list4.GetColumn(j, "ZZLIFNR") != ds_list4.GetOrgColumn(j, "ZZLIFNR") ||
													ds_list4.GetColumn(j, "ZZTEMNO") != ds_list4.GetOrgColumn(j, "ZZTEMNO") ||
													ds_list4.GetColumn(j, "ZZCOMP2") != ds_list4.GetOrgColumn(j, "ZZCOMP2")
												   )
		  )
		{
			ds_list4_1.AddRow();
			ds_list4_1.CopyRow(rows, ds_list4, j);
			rows++;
		}
	}

	//Trace(ds_list4_1.saveXML());

	// 저장 처리
	tit_callService(
        "PS0201001A_ACT"
        , "change"
        , "ds_list=ds_list4:A ds_cond=ds_cond_bak"
        , "ds_error=ds_error ds_list4=ds_list"
        , "GUBUN='I'"
        , "fn_afterSave5");
}

function fn_afterSave5(errCode, errMsg)
{
	//Trace(ds_list4_1.saveXML());
	gfn_showWSSaveMsgForQM("Tab00.tab3.grd_list", ds_error, "FLAG", errCode, errMsg);

	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps02:PS0201001C_S46", "ps02:PS0201001C_I42", "ps02:PS0201001C_U42");   //zmaster02
	tit_addSelectExActionInfo("ps02:PS0201001C_S47", , "ps02:PS0201001C_U43");   //zpst0010
	tit_addSelectExActionInfo("ps02:PS0201001C_S49", "ps02:PS0201001C_I43", "ps02:PS0201001C_U45");    //zpst0102p

	tit_callService(
		""
		, ""
		, "ds_list=ds_list4_1:A"
		, ""
		, ""
		, "fn_afterSave21");
}
