	{
		ds_master.SetColumn(0, "FRAME_AFQ", '');
	}
	else	// 수정
	{
		if(nFrameAfq == 0 && sAfqIndc == '')	// 층수데이터가 0이고, 층수 데이터 변경을 하지 않았다면
		{
			ds_master.SetColumn(0, "FRAME_AFQ", '');
		}
	}

	//# 특기사항 + (G2미통과이고, Interface 안된 호기이면 = 'X') EN_INTER = 'X'인 경우 

	/*
	tit_clearActionInfo();
	tit_callService(
          "PS0101001A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list1=ds_list"
        , ""
        , "fn_afterQuery1"
        , false);

    */

	//# 이미지 가져오기
	ufn_getConsultImage();


}

