/********************************************************************************
* 기      능   : 이미지 파일일 경우 열어줌
********************************************************************************/
function ufn_isImage(fileNm) {
	var s = toUpperCase(right( fileNm, 4));
	if ( s == ".JPG" || s == ".GIF" || s == ".PNG" || s == ".BMP") {
		return true;
	} else if ( toUpperCase(right( fileNm, 5)) == ".JPEG") {
		return true;
	}

	return false;
}

