function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

   //PM지정일 : 현재월의 첫날짜, 현재날짜
	var firstDay = substr(gfn_getCurrDate(),0,6)+"01";
	var lastDay = gfn_getCurrDate();

	ufn_fileWidthHeightChk();

	Tab00.TabIndex = 0;
	// 설치 준공 확정 권한 체크(2016.04.12 추가--LEH)
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S71");
	tit_callService(
      ""
	, ""
	, "ds_cond_cd=ds_cond_cd"
	, "ds_autal=ds_autal"
	, ""
	, "fn_auth"
	, true,true);

}
/********************************************************************************
* 기      능   :  권한 체크 
********************************************************************************/
function fn_auth()
{   
     //설치 준공 확정 담당자 권한 부여 
    if(ds_autal.GetColumn(0,"AUTAL") == 'X')
    {  //alert("AA");
       userGroup = '40';
    }
    //권한 별 활성/비활성 
  	if( userGroup == '10' )
	{
	    chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;

		Tab00.tab3.grd_list.Format = "Default";
		Tab00.tab4.grd_list.Format = "Default";
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S11");
		tit_addSearchActionInfo("ps02:PS0201001A_S51");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond_cd"
			, "ds_zzlifnr_cd=ds_zzlifnr_cd ds_gubun=ds_gubun"
			, ""
			, "fn_afterZzlifnrSearch"
			, true,true);
	}
	else if( userGroup == '30' )
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;

		Tab00.tab3.grd_list.Format = "Default30";
		Tab00.tab4.grd_list.Format = "Default30";
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S31");
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_addSearchActionInfo("ps02:PS0201001A_S33");
		tit_addSearchActionInfo("ps02:PS0201001A_S51");
		tit_callService(
			""
			, ""
			, "ds_cond_cd=ds_cond_cd"
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd ds_zzlifnr_cd=ds_zzlifnr_cd ds_gubun=ds_gubun"
			, ""
			, "fn_afterZzactssSearch"
			, true,true);
	}
	else if( userGroup == '40' )
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;

		Tab00.tab3.grd_list.Format = "Default40";
		Tab00.tab4.grd_list.Format = "Default40";
		//코드콤보 설정 : 지역, 구분2
		var codeArr = ["PS003"];
		var dsObjArr = [ds_zzactss_cd];
		var addTypeArr = [G_CODE_ALL];
		gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S33");
		tit_addSearchActionInfo("ps02:PS0201001A_S51");
		tit_callService(
			""
			, ""
			, "ds_cond_cd=ds_cond_cd"
			, "ds_zzlifnr_cd=ds_zzlifnr_cd ds_gubun=ds_gubun"
			, ""
			, "fn_afterSearchAll"
			, true,true);

	}
	//권한에 따라 조회조건 필드 활성화/비활성화
	fn_authCheck('0');
	btn_gis.Enable = false;
/*	
	if ( G_USER_ID != '2018900' )
	{	
*/
		btn_naver.Enable 	= false;
		btn_naver.Visible	= false;
/*
	}
*/
	gfn_showMsg("CI00007");

}

function fn_authCheck(flg)
{
	if(flg == '0')
	{
		ufn_enableBtn("query", true);
		ufn_enableBtn("save", false);
		ufn_enableBtn("delete", false);
		cal_datef.Enable = true;
		cal_datet.Enable = true;
		ed_pspid.Enable = true;
		cb_mode.Enable = true;
		if( userGroup == '10' )
		{
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = false;
			cb_zzlifnr.Enable = false;
		}
		else if( userGroup == '30' )
		{
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = true;
			cb_zzlifnr.Enable = true;
		}
		else if( userGroup == '40' )
		{
			cb_zzactss.Enable = true;
			cb_zzpmnum.Enable = true;
			cb_zzlifnr.Enable = true;
		}
		else
		{
			cb_zzactss.Enable = false;
			cb_zzpmnum.Enable = false;
			cb_zzlifnr.Enable = false;
		}
	}
	else
	{
		cal_datef.Enable = false;
		cal_datet.Enable = false;
		ed_pspid.Enable = false;
		cb_mode.Enable = false;
		cb_zzactss.Enable = false;
		cb_zzpmnum.Enable = false;
		cb_zzlifnr.Enable = false;
	}
}
/********************************************************************************
* 기      능   : 사무소콤보 변경시 설치PM 콤보값 생성
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

if(userGroup == "40" || userGroup == "30"){
	ds_cond.SetColumn(0,"ZZLIFNR",'');  }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_zzlifnr_cd.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_zzlifnr_cd.Filter("ZZACTSS ==" +quote(arr));
    }


	ds_cond_cd.SetColumn(0,"ZZACTSS",strCode);
	cb_zzpmnum.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S41");
	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond_cd"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, "fn_afterZzactssChange"
		, true,true);
}
/********************************************************************************
* 기      능   : 그리드의 사무소콤보 변경시 설치PM 콤보값 변경
