	ds_cond_cd.SetColumn(0,"TEMNO",strCode);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S33");
	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond_cd"
		, "ds_zzlifnr_cd=ds_zzlifnr_cd"
		, ""
		, ""
		, true,true);
}
*/
