/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	if(Tab00.TabIndex == 0)
		fn_query1();
	else if(Tab00.TabIndex == 1)
		fn_query2();
	else if(Tab00.TabIndex == 2)
		fn_query3();
	else if(Tab00.TabIndex == 3)
		fn_query4();
}
/********************************************************************************
* 기      능   : 조회(프로젝트 정보)
********************************************************************************/
function fn_query1() {
	// 필수 입력사항 및 일자 체크
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) ) {
		return;
	}
	//설치협력사 조회시 협력업체 필수체크
	if(userGroup == '10' && gfn_isNull(cb_zzlifnr, true, st_zzlifnr.value)) {
		return;
	}
	//사무소 조회시 사무소 필수체크
	if(userGroup == '30' && gfn_isNull(cb_zzactss, true, st_zzactss.value)) {
		return;
	}
	if(cal_datef.Value == '' ){
		//프로젝트필드 필수체크
		if(gfn_isNull(ed_pspid, true, st_pspid.value)) return;
	}
	//구분필드 필수체크
	if(gfn_isNull(cb_mode, true, st_mode.value)) {
		return;
	}
	ds_cond.SetColumn(0,"TAB_GBN","1");
	// sort mark 삭제
	gfn_clearGridSortMark(Tab00.tab1.grd_list);
	//ds_list.row = -1;
	// 조회 처리
	tit_callService(
        "PS0201001A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , false); 
}
/********************************************************************************
* 기      능   : 조회(현장답사보고서)
********************************************************************************/
function fn_query2() {
  	//프로젝트필드 필수체크
	if(gfn_isNull(ed_pspid, true, st_pspid.value)) return;
	//구분필드 필수체크
	if(gfn_isNull(cb_mode, true, st_mode.value)) return;
	// 필수 입력사항 및 일자 체크
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) ) return;
	//설치협력사 조회시 협력업체 필수체크
	if(userGroup == '10' && gfn_isNull(cb_zzlifnr, true, st_zzlifnr.value)) return;
	//사무소 조회시 사무소 필수체크
	if(userGroup == '30' && gfn_isNull(cb_zzactss, true, st_zzactss.value)) return;

	ds_cond2.SetColumn(0,"SEQNO",TAB00.tab2.cb_chasu.Value);
    ds_cond.SetColumn(0,"TAB_GBN","4");

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001B_S1");
	tit_addSearchActionInfo("ps02:PS0201001B_S2");
	tit_addSearchActionInfo("ps02:PS0201001B_S4");

	//*김연식 대리가 핀제거 막아달라고 요청함*//
	//tit_addSearchActionInfo("ps02:PS0201001B_S5");


	tit_addSearchActionInfo("ps02:PS0201001B_S6");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        //*김연식 대리가 핀제거 막아달라s고 요청함*//
        //, "ds_list2=ds_list2 ds_list3=ds_list3 ds_cellp=ds_cellp ds_pin=ds_pin ds_chk=ds_chk"
        , "ds_list2=ds_list2 ds_list3=ds_list3 ds_cellp=ds_cellp ds_chk=ds_chk"
        , ""
        , "fn_afterQuery2"
        , true);
}
/********************************************************************************
* 기      능   : 조회(착준공계획 입력)
********************************************************************************/
function fn_query3() {

	// 필수 입력사항 및 일자 체크
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) ) return;
	//설치협력사 조회시 협력업체 필수체크
	if(userGroup == '10' && gfn_isNull(cb_zzlifnr, true, st_zzlifnr.value)) return;
	//사무소 조회시 사무소 필수체크
	if(userGroup == '30' && gfn_isNull(cb_zzactss, true, st_zzactss.value)) return;
	//구분필드 필수체크
	if(gfn_isNull(cb_mode, true, st_mode.value)) return;
	//프로젝트필드 필수체크
	if(gfn_isNull(ed_pspid, true, st_pspid.value)) return;

	ds_cond.SetColumn(0,"TAB_GBN","2");
	// sort mark 삭제
	gfn_clearGridSortMark(Tab00.tab3.grd_list);
	//ds_list.row = -1;
	// 조회 처리
	tit_callService(
        "PS0201001A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list4=ds_list"
        , ""
        , "fn_afterQuery3"
        , false);
}
/********************************************************************************
* 기      능   : 조회(착준공계획 수정)
********************************************************************************/
function fn_query4() {
	//설치협력사 조회시 협력업체 필수체크
	if(userGroup == '10' && gfn_isNull(cb_zzlifnr, true, st_zzlifnr.value)) return;
	//사무소 조회시 사무소 필수체크
	if(userGroup == '30' && gfn_isNull(cb_zzactss, true, st_zzactss.value)) return;
	//구분필드 필수체크
	if(gfn_isNull(cb_mode, true, st_mode.value)) return;
	//프로젝트필드 필수체크
	if(gfn_isNull(ed_pspid, true, st_pspid.value)) return;

	ds_cond.SetColumn(0,"TAB_GBN","3");
	// sort mark 삭제
	gfn_clearGridSortMark(Tab00.tab4.grd_list);
	//ds_list.row = -1;

	ds_list5.CanRowPosChange = "";
	// 조회 처리
	tit_callService(
        "PS0201001A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list5=ds_list"
        , ""
        , "fn_afterQuery4"
        , false);
}
