/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if(Tab00.TabIndex == 1) fn_save1();
	else if(Tab00.TabIndex == 2) fn_save2();
	else if(Tab00.TabIndex == 3) fn_save3();
}
/********************************************************************************
* 기      능   : 저장(현장답사 보고서)
********************************************************************************/
function fn_save1() {
//TRACE(ds_list3.SaveXML());

	if ( inUpFlg == "") {
		gfn_showMsg("CW00010");
		return;
	}

	//else if ( inUpFlg == "U" && !gfn_isChangeDs(ds_list3, true, "")){
		//return;
	//}
	// 현장답사일 필수체크
	if ( length( ds_list3.getColumn(0, "EXPLO")) == 0 ) {
		gfn_showMsg("CW00002");
		Tab00.tab2.cal_explo.setfocus();
		return;
	}// 현장답사자 필수체크
	else if ( length( ds_list3.getColumn(0, "EXPLM")) == 0 ) {
		gfn_showMsg("CW00002");
		Tab00.tab2.ed_explm.setfocus();
		return;
	}// 현장답사예정일 필수체크(현장협의완료 체크 X 일떄만)
	if(length( ds_list3.getColumn(0, "EXPLF")) == 0)
	{
 	  if ( length( ds_list3.getColumn(0, "EXPLD")) == 0 ) {
		   gfn_showMsg("CW00002");
		   Tab00.tab2.cb_expld.setfocus();
		   return;
	   }    
	}

	/*
	else if ( length( ds_list3.getColumn(0, "COMPL")) == 0 ) {
		gfn_showMsg("CW00002");
		Tab00.tab2.cal_compl.setfocus();
		return;
	}// 건축골조진행현황 필수체크
	else if ( length( ds_list3.getColumn(0, "RESEA")) == 0 ) {
		gfn_showMsg("CW00002");
		Tab00.tab2.ed_resea.setfocus();
		return;
	}
	else if ( ds_chk.GetColumn(0,"CNT") > '0' && ds_pin.getColumn(0, "PINYN") != 'Y' ) {
		gfn_showMsg("CW00227");
		Tab00.tab2.cb_pinyn.setfocus();
		return;
	}
    */


	var pjt = ds_list3.GetColumn(0, "PSPID");
	var EXPLO = ds_list3.GetColumn(0, "EXPLO");
	var EXPLM = ds_list3.GetColumn(0, "EXPLM");    

	tit_clearActionInfo();

	//김연식 대리가 핀제거 막아달라고 요청함.
	//tit_addSingleActionInfo("ps02:PS0201001B_U2"); //핀제거 완료/미완료 여부


	if(inUpFlg == "I")
	 { 
	   tit_addMultiActionInfo("ps02:PS0201001B_I1");  // 신규입력
	 }
	else if (inUpFlg == "U") 
	{
	  tit_addMultiActionInfo("ps02:PS0201001B_U1");// 수정
	}

	tit_callService(
		""
		, ""
		//김연식 대리가 핀제거 막아달라고 요청함.
		//, "ds_list3=ds_list3:A ds_pin=ds_pin"
		, "ds_list3=ds_list3"
		, ""
		, ""
		, "fn_SaveFile"
		, ""
		, ""
		, true);	

}

/********************************************************************************
* 기      능   : 저장(착준공대상 입력)
********************************************************************************/
function fn_save2() {
	if ( ds_list6.rowcount == 0 )
	{
		gfn_showMsg("CW00066");
		return;
	}

//alert( ds_list9.GetColumn(0,"FNAME"));
	if ( ds_list9.GetColumn(0,"FNAME") <> 'O' )
	{

		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "현장답사 협의록을 사전에 등록하여야, 착준공계획정보를 입력할 수 있습니다.";
		return;	

	}

	if ( !gfn_isChangeDs(ds_list4, true, "")){
		return;
	}

	for( var i=0; i<ds_list4.rowcount; i++ )
	{
		if (ds_list4.GetColumn(i,"FLAG") == "I")
		{

			if ( length(ds_list4.getColumn(i, "AUFNR1")) > 0 &&
			    ( length(ds_list4.getColumn(i, "ZZSHIP1")) == 0 || ds_list4.getColumn(i, "ZZSHIP1") == "0000-00-00")) {
				gfn_showMsg("CW00001",st_zzship1.value);
				ds_list4.row = i;
				return;
			}

			else if ( length(ds_list4.getColumn(i, "AUFNR2")) > 0 &&
					 (length(ds_list4.getColumn(i, "ZZSHIP2")) == 0 || ds_list4.getColumn(i, "ZZSHIP2") == "0000-00-00" )) {
				gfn_showMsg("CW00001",st_zzship2.value);
				ds_list4.row = i;
				return;
			}
			else if ( length(ds_list4.getColumn(i, "AUFNR3")) > 0 &&
					 (length(ds_list4.getColumn(i, "ZZSHIP3")) == 0 || ds_list4.getColumn(i, "ZZSHIP3") == "0000-00-00" )) {
				gfn_showMsg("CW00001",st_zzship3.value);
				ds_list4.row = i;
				return;
			}
			else if ( length(ds_list4.getColumn(i, "AUFNR4")) > 0 &&
					  (length(ds_list4.getColumn(i, "ZZSHIP4")) == 0 || ds_list4.getColumn(i, "ZZSHIP4") == "0000-00-00" )) {
				gfn_showMsg("CW00001",st_zzship4.value);
				ds_list4.row = i;
				return;
			}
			else if ( length(ds_list4.getColumn(i, "AUFNR5")) > 0 &&
					 (length(ds_list4.getColumn(i, "ZZSHIP5")) == 0 || ds_list4.getColumn(i, "ZZSHIP5") == "0000-00-00" )) {
				gfn_showMsg("CW00001",st_zzship5.value);
				ds_list4.row = i;
				return;
			}
			else if ( length(ds_list4.getColumn(i, "AUFNR6")) > 0 &&
					 (length(ds_list4.getColumn(i, "ZZSHIP6")) == 0 || ds_list4.getColumn(i, "ZZSHIP6") == "0000-00-00" )) {
				gfn_showMsg("CW00001",st_zzship6.value);
				ds_list4.row = i;
				return;
			}
			//준공예정일 체크
			if(replace(ds_list4.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list4.getColumn(i, "ZZSHIP1"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list4.row = i;
				return;
			}
			else if(replace(ds_list4.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list4.getColumn(i, "ZZSHIP2"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list4.row = i;
				return;
			}
			else if(replace(ds_list4.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list4.getColumn(i, "ZZSHIP3"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list4.row = i;
				return;
			}
			else if(replace(ds_list4.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list4.getColumn(i, "ZZSHIP4"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list4.row = i;
				return;
			}
			else if(replace(ds_list4.getColumn(i, "ZZCOMP2"),'-','') < replace(ds_list4.getColumn(i, "ZZSHIP5"),'-','') )
			{
				var arr = [st_zzcomp2.value, st_zzship.value ];
				gfn_showMsg("CW00007", arr);
				ds_list4.row = i;
				return;
			}

			if( userGroup == '30' && length(ds_list4.getColumn(i, "ZZPMNUM")) == 0)
			{
				gfn_showMsg("CW00001",st_zzpmnum.value);
				ds_list4.row = i;
				return;
			}
			else if( userGroup == '30' && length(ds_list4.getColumn(i, "ZZLIFNR")) == 0)
			{
				gfn_showMsg("CW00001",st_zzlifnr.value);
				ds_list4.row = i;
				return;
			}
			/*else if( userGroup == '30' && length(ds_list4.getColumn(i, "ZZLIFNR2")) == 0)
			{
				gfn_showMsg("CW00001",st_zzlifnr2.value);
				ds_list4.row = i;
				return;
			}
			*/
			else if( userGroup == '40' && length(ds_list4.getColumn(i, "ZZACTSS")) == 0)
			{
				gfn_showMsg("CW00001",st_zzactss.value);
				ds_list4.row = i;
				return;
			}
			else if( userGroup == '40' && length(ds_list4.getColumn(i, "ZZPMNUM")) == 0)
			{
				gfn_showMsg("CW00001",st_zzpmnum.value);
				ds_list4.row = i;
				return;
			}
			else if( userGroup == '40' && length(ds_list4.getColumn(i, "ZZLIFNR")) == 0)
			{
				gfn_showMsg("CW00001",st_zzlifnr.value);
				ds_list4.row = i;
				return;
			}
			/*
			else if( userGroup == '40' && length(ds_list4.getColumn(i, "ZZLIFNR2")) == 0)
			{
				gfn_showMsg("CW00001",st_zzlifnr2.value);
				ds_list4.row = i;
				return;
			}
			*/
		}
	}

	//jcbc : zmster01,zmaster02 insert,update하기위해 데이타셋 생성
	var rows = 0;
	for(var j=0; j<ds_list4.rowcount; j++)
	{
		if(ds_list4.getcolumn(j,"FLAG") == "I")
		{
			ds_list4_1.AddRow();
			ds_list4_1.CopyRow(rows, ds_list4, j);
			rows++;
		}
	}

	// 저장 처리
	tit_callService(
        "PS0201001A_ACT"
        , "save"
        , "ds_list=ds_list4:A ds_cond=ds_cond_bak"
        , "ds_error=ds_error ds_list4=ds_list"
        , "GUBUN='I'"
        , "fn_afterSave2");


}



/********************************************************************************
* 기      능   :  출하물량 집중, 요청 일자 불가
********************************************************************************/
function fn_checkLoadDay()
{
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S1");      

	tit_callService(
		""
		, ""
		, "ds_cond_day=ds_cond_day"
		, "ds_chk=ds_chk"
		, ""
		, ""
		, ""  //true
		, ""
		, true);


	if( ds_chk.GetColumn(0, "CNT") = 0 )
	{
		return false;
	}
	else
	{
		return true;
	}


}




/********************************************************************************
* 기      능   : 영업일체크(rfc)
********************************************************************************/
function fn_checkWorkDay()
{
	// workday조회
	tit_callService(
        "COMM_ACT"
        , "getPsWorkingDay"
        , ""
        , ""
        , "I_CHECK_DATE=" + quote(ds_cond_day.GetColumn(0,"I_CHECK_DATE"))
        , "fn_aftercheckWorkDay"
        ,true,true,true);
}


/********************************************************************************
* 기      능   : 근무일 17일 이후
********************************************************************************/
function fn_WorkDay_17(spart,sp_ause)
{
    //신성철, 김항진 기장, 조용준 + 김도윤 추가(신성철씨요청)
	if(G_USER_ID == '3018598' or G_USER_ID == '2026490' or G_USER_ID == '2021757'or G_USER_ID == '3012751' or G_USER_ID == '2019094' or G_USER_ID == '2006227' or G_USER_ID == '3017044' or G_USER_ID == '3016552' or G_USER_ID == '3011534' or G_USER_ID == '3013383'or G_USER_ID == '3012255' or G_USER_ID == '3016196' or G_USER_ID == '2016885'or G_USER_ID == '3016200'  or spart == 'Y'
	    or G_USER_ID == '2026627'  or G_USER_ID == '2026660'  or G_USER_ID == '2026350' or sp_ause == 'Y')//or G_USER_ID == '2022702') 
	{     //우제석대리의 요청으로 이무열BJ, 이대인JJ, 이병만JJ,이용범,윤휘중,김대희,이재현 추가
	      //우제석 대리 요청으로 박한님sw 추가
	      // 신성철씨 요청으로 신기성씨 추가 
	      // 신기성씨 요청으로 김택영씨 추가 
	      // 우제석 DR 영업으로 이동 -> 권한 삭제
          //김항진 기장 추가
          // 신기성씨 요청으로 신입사원 임세준 /김태환/김동훈 추가
          //우동훈 GJ 요청 추가 자재 17일 이전 선택 추가 
		return true;
	}
	else
	{
		if( ToNumber(gfn_getWorkingDate(17,"1000",gfn_getCurrDate()))
		    <= ToNumber(ds_cond_day.GetColumn(0,"I_CHECK_DATE")) )
		{
			return true;
		}
		else
		{
			return false;
		}

	}

}


