/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_SaveFile(errCode, errMsg) {


 if(ds_list3.FindRow("CHECK", 1)==-1)
 { //alert("a");
   ufn_getChasu();
 }

 else {
//alert("b");
   if(fn_checkSave()){

//----- 저장전 확장자 확인  -- 2015.06.02 L.E.H------------

 var thumbext = ds_list3.GetColumn(0, "FNAME"); //파일을 추가한 input 값

     thumbext = substr(thumbext, thumbext.indexOf(".") + 1); //확정자값 가져오기 

     thumbext = ToLower(thumbext); //소문자로 변경


  if(thumbext != "jpg" && thumbext != "png" &&  thumbext != "gif" &&  thumbext != "bmp"  && thumbext != "pdf"){ // 확장자를 확인

     global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
	 global.FRM_BOTTOM.st_msg.color = "yellow";       
	 global.FRM_BOTTOM.st_msg.value = "이미지 파일(jpg, png, gif, bmp, pdf)만 등록 가능합니다.";
	 return false;
  }
//----------------------------------------------------------



    var fileName = "";
		//fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list_tmp.getColumn(0, "FNAME");
		fileName = left(GetDate(),8)+"_"+right(GetDate(),6)+"_"+ds_list3.getColumn(0, "SEQNO2")+"_"+ds_list3.getColumn(0, "PSPID");

		var arr = tit_fileWrite(httpFile
						, "PS"
						, ds_list3.getColumn(0, "FPATH")
						, ds_list3.getColumn(0, "FNAME")
						, fileName// 실제 db에 저장될 파일 이름
						, "U"	  // time stamp를 붙이지 않을 경우 U 사용 
						, "");    // ds_list_tmp.getColumn(i, "CS108_QAD") );//삭제할 이전 파일명


		if ( arr[0] == "S" ) 
		{
	 		   ds_list3.setColumn(0, "FNAME_APP", arr[2]);
			   tit_clearActionInfo();
			   if(inUpFlg == "U") //수정
			   {
			      tit_addMultiActionInfo("ps02:PS0201001B_U3");		                       
			   }
			   else if(inUpFlg == "I") //신규
			   {
			      tit_addMultiActionInfo("ps02:PS0201001B_U5");		     
			   }
			   tit_callService(
			     ""
			   , ""
			   , "ds_list3=ds_list3"
			   , ""
			   , ""
			   , "fn_afterSave1" 
			   , ""
			   , ""
			   , true);	

		}

	   else if ( arr[0] != "S" )
	   {
		  gfn_showMsg("CW00035"); 
		  return;
	   }


     }						
 }

	//fn_afterSave1();
}
