/********************************************************************************
* 기      능   : 사용자 정의 함수
********************************************************************************/


//=========================================================================================
// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트
//=========================================================================================
/********************************************************************************
* 기      능   : 텝 선택시
********************************************************************************/
function Tab00_OnChanged(obj, nOldIndex, nNewIndex)
{
	if( obj.TabIndex == 1 ) {

		fn_authCheck('1');
    	btn_gis.Enable = true;    	
    	if ( G_USER_ID == '2018900' )
		{	
			btn_naver.Enable 	= true;
			btn_naver.Visible	= true;
		}


		if(ds_list.rowcount > 0 )
		{
		  ufn_enableBtn("query", true);
		  ufn_enableBtn("save", true);
		  ufn_enableBtn("delete", true);
		}
		else
		{
		  ufn_enableBtn("query", false);
		  ufn_enableBtn("save", false);
		  ufn_enableBtn("delete", false);
		}
		//btn_gis.Enable = true;

		if(ds_list.rowcount == 0 || ds_list.GetColumn(ds_list.row,"POSID") == "")
			ds_cond2.SetColumn(0,"PSPID","");
		else
			ds_cond2.SetColumn(0,"PSPID",ds_list.GetColumn(ds_list.row,"POSID"));

		ufn_getChasu();
	}
	else if( obj.TabIndex == 2 ) {

		fn_authCheck('1');
		btn_gis.Enable = false;		

		//if ( G_USER_ID != '2018900' )
		//{				
			btn_naver.Enable 	= false;
			btn_naver.Visible	= false;			
		//}


		if(ds_list.rowcount > 0 && cb_mode.value == "1")
		{
			ufn_enableBtn("query", true);
			ufn_enableBtn("save", true);
			ufn_enableBtn("delete", false);
		}
		else
		{
			ufn_enableBtn("query", false);
			ufn_enableBtn("save", false);
			ufn_enableBtn("delete", false);

		}
		if(ds_list.rowcount == 0 || ds_list.GetColumn(ds_list.row,"POSID") == "")
			ds_cond.SetColumn(0,"PSPID","");
		else
			ds_cond.SetColumn(0,"PSPID",ds_list.GetColumn(ds_list.row,"POSID"));
	}
	else if( obj.TabIndex == 3 ) {
		fn_authCheck('1');
		btn_gis.Enable = false;

		//if ( G_USER_ID != '2018900' )
		//{					
			btn_naver.Enable 	= false;
			btn_naver.Visible	= false;
		//}


		if(ds_list.rowcount > 0 && cb_mode.value == "2")
		{
			ufn_enableBtn("query", true);
			ufn_enableBtn("save", true);
			ufn_enableBtn("delete", false);
		}
		else
		{
			ufn_enableBtn("query", false);
			ufn_enableBtn("save", false);
			ufn_enableBtn("delete", false);
		}
		if(ds_list.rowcount == 0 || ds_list.GetColumn(ds_list.row,"POSID") == "")
			ds_cond.SetColumn(0,"PSPID","");
		else
		{
			ds_cond.SetColumn(0,"PSPID",ds_list.GetColumn(ds_list.row,"POSID"));

			tit_clearActionInfo();
			tit_addSearchActionInfo("ps02:PS0201001D_S1");
			tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_list_h=ds_list"
				, ""
				, "fn_afterQueryH"
				, true);
		}

	}
	else {
		ufn_enableBtn("query", true);
		ufn_enableBtn("save", false);
		ufn_enableBtn("delete", false);
		btn_gis.Enable = false;

		//if ( G_USER_ID != '2018900' )
		//{	
			btn_naver.Enable 	= false;
			btn_naver.Visible	= false;
		//}



		fn_authCheck('0');

		//주석 : 조회텝으로 이동후 조회조건 필드에 사무소,협력업체 코드 "" 로 셋팅한다.
		ds_list.row = row;
		ds_cond.SetColumn(0,"PSPID",pspid );

		///////////////////////권한이 전체 일경우만 아래 로직실행///////////////////////
		if (userGroup == '40')
		{
			ds_cond.SetColumn(0,"ZZACTSS",actss );
		}
		//ds_cond.SetColumn(0,"ZZACTSS",actss );
		////////////////////////////////////////////////////////////////////////////////

		ds_cond.SetColumn(0,"ZZPMNUM",pmnum );
		ds_cond.SetColumn(0,"ZZLIFNR",lifnr );
	}
}

function ufn_getChasu()
{
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001B_S3");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond2"
		, "ds_chasu=ds_chasu"
		, ""
		, "fn_afterChasu"
		, true);			
}
