/********************************************************************************
* 기      능   :  출하요청일 입력여부
********************************************************************************/
function fn_changeTf1(currow,aufnr,zzfkeyd,zzstan1)
{
	//TXT04값이 E(정산),F(완료),C(취소) 인 경우 수정 불가 -20090827(방인호D 요청)
	if(ds_list5.GetColumn(currow,"TXT04") == 'E' || ds_list5.GetColumn(currow,"TXT04") == 'F' || ds_list5.GetColumn(currow,"TXT04") == 'C') return false;
	if(length(zzfkeyd) > 0 || (length(zzstan1) > 0 && trim(zzstan1) == '1')) return false;
	else return true;
}
function fn_changeTf2(currow,aufnr,zzfkeyd,zzstan1)
{
	//TXT04값이 E(정산),F(완료),C(취소) 인 경우 수정 불가 -20090827(방인호D 요청)
	if(ds_list5.GetColumn(currow,"TXT04") == 'E' || ds_list5.GetColumn(currow,"TXT04") == 'F' || ds_list5.GetColumn(currow,"TXT04") == 'C') return false;
	if(aufnr > '' && (length(zzstan1) == 0 || trim(zzstan1) == '0')) return true;
	else return false;
}
function fn_changeTf6(currow,aufnr)
{
	//TXT04값이 E(정산),F(완료),C(취소) 인 경우 수정 불가 -20090827(방인호D 요청)
	if(ds_list5.GetColumn(currow,"TXT04") == 'E' || ds_list5.GetColumn(currow,"TXT04") == 'F' || ds_list5.GetColumn(currow,"TXT04") == 'C') return false;
	if(aufnr > '' ) return true;
	else return false;
}
function fn_changeTf3(currow,flg)
{
	//TXT04값이 E(정산),F(완료),C(취소) 인 경우 수정 불가 -20090827(방인호D 요청)
	if(ds_list5.GetColumn(currow,"TXT04") == 'E' || ds_list5.GetColumn(currow,"TXT04") == 'F' || ds_list5.GetColumn(currow,"TXT04") == 'C') return false;
	if(length(flg) > 0 ) return false;
	else return true;
}
/********************************************************************************
* 기      능   :  데이타셋(ds_list5) 로우 변경시
********************************************************************************/
function ds_list5_CanRowPosChange(obj,nOldRow,nRow)
{
	//GUBUN = POP UP에서 변경사유를 저장하면 'Y'로 변경시켜준다.
	if(length(ds_list5.GetColumn(nOldRow,"ZZCHAKG")) == 0 &&
	   ds_list5.GetColumn(nOldRow,"ZZFMAGA") == "X" &&
	   ds_list5.GetOrgColumn(nOldRow,"ZZSHIP1") != ds_list5.GetColumn(nOldRow,"ZZSHIP1") &&
	   length(ds_list5.GetColumn(nOldRow,"GUBUN")) == 0)
	{
		gfn_showMsg("CW00049","착공예정일");
		return false;
	}
	else if(length(ds_list5.GetColumn(nOldRow,"ZZJUNGO")) == 0 &&
	        ds_list5.GetColumn(nOldRow,"ZZSTAN2") == "X" &&
	        ds_list5.GetOrgColumn(nOldRow,"ZZCOMP2") != ds_list5.GetColumn(nOldRow,"ZZCOMP2") &&
	        length(ds_list5.GetColumn(nOldRow,"GUBUN")) == 0)
	{
		gfn_showMsg("CW00049","준공예정일");
		return false;
	}
	else
		return true;
}

function Tab00_tab1_grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	row = ds_list.row;
}
