					ds_list5.SetColumn(i,"ZZSHIP5",ds_list5.GetColumn(N_ROW,"ZZSHIP5"));
				else if(N_CELL == 13 && length(ds_list5.GetColumn(i,"AUFNR6")) > 0)
					ds_list5.SetColumn(i,"ZZSHIP6",ds_list5.GetColumn(N_ROW,"ZZSHIP6"));
				else if(N_CELL == 16 && length(ds_list5.GetColumn(i,"ZZJUNGO")) == 0 && length(ds_list5.GetColumn(i,"ZZSTAN2")) == 0)
				{
					ds_list5.SetColumn(i,"ZZCOMP2",ds_list5.GetColumn(N_ROW,"ZZCOMP2"));
				}
			}
		}
	}
	N_COPY = "N";
}
*/
function Tab00_tab4_grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	N_CELL = nCell;
	N_ROW = nRow;
}

