function btn_copy_OnClick(obj)
{
	N_COPY = "Y"; //DATASET변경시 FLAG가 'Y'인 경우 휴일체크 안함

	if(Tab00.TabIndex == 2) {
		var arg = "tGrid=Tab00.tab3.grd_list";
		gfn_openPopup("PS9602002A", true, arg);
	} else if(Tab00.TabIndex == 3) {
		var arg = "tGrid=Tab00.tab4.grd_list";
		gfn_openPopup("PS9602002A", true, arg);
	} else {
		return;
	}

	N_COPY = "N";
}

