function Tab00_tab4_grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(userGroup != '40') return;

	var arg = "";

	/*
	if(nCell == 8 && length(trim(ds_list5.GetColumn(nRow,"ZZCHAKG"))) == 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP1'";
	}
	*/
	if(nCell == 8 && length(trim(ds_list5.GetColumn(nRow,"AUFNR1"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP1'";
	}

	else if(nCell == 9 && length(trim(ds_list5.GetColumn(nRow,"AUFNR2"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP2'";
	}
	else if(nCell == 10 && length(trim(ds_list5.GetColumn(nRow,"AUFNR3"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP3'";
	}
	else if(nCell == 11 && length(trim(ds_list5.GetColumn(nRow,"AUFNR4"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 ) 
	{
		arg = "date_='ZZSHIP4'";
	}	
	else if(nCell == 12 && length(trim(ds_list5.GetColumn(nRow,"AUFNR5"))) > 0 && length(trim(ds_list5.GetColumn(nRow,"ZZSTAN1"))) == 0 )
	{
		arg = "date_='ZZSHIP5'";
	}
	else if(nCell == 13 && length(trim(ds_list5.GetColumn(nRow,"AUFNR6"))) > 0)
	{
		arg = "date_='ZZSHIP6'";
	}
	else if(nCell == 16 && length(trim(ds_list5.GetColumn(nRow,"(ZZSTAN1)"))) == 0)
	{
		arg = "date_='ZZCOMP2'";
	}
	else
	{
		return;
	}
	gfn_openPopup("PS9602003A", true, arg);

}

