function Tab00_tab2_Grid0_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
//function Tab00_tab2_grd_list2_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
//function Tab00_tab2_grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
  if ( nCell == 4 )
		if ( length(ds_list3.getColumn(0, "FNAME")) > 0) {
			// 첨부 파일 처리 

			saveDialog.FileName = ds_list3.getColumn(0, "FNAME");//tit_getLocalFileName(ds_list2.getColumn(nRow, "FNAME"));

			if (saveDialog.open())
			{
				tit_fileRead(
					httpFile
					, "PS"
					, ds_list3.getColumn(nRow, "FNAME_APP")
					, saveDialog.FilePath
					, saveDialog.FileName);
			}
		}
	}

}


/********************************************************************************
* 기      능   : 저장전에 자료 검증 
********************************************************************************/
function fn_checkSave()
{
//alert("yyy");
		ds_cond3.ClearData();
		ds_cond3.AddRow();
	//	ds_cond3.setColumn(0, "GBN", ds_list_tmp.GetColumn(i, "GBN"));
		ds_cond3.setColumn(0, "PSPID", ds_list3.GetColumn(0, "PSPID"));
		ds_cond3.setColumn(0, "SEQNO", ds_list3.GetColumn(0, "SEQNO"));
		ds_cond3.setColumn(0, "FNAME", ds_list3.GetColumn(0, "FNAME"));

		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S12");		   
        tit_addSearchActionInfo("ps02:PS0201001B_S7");	
		tit_callService(
			""
			, ""
			, "ds_cond3=ds_cond3"
			, "ds_list8=ds_list8 ds_list7=ds_list7"
			, ""
			, ""
			, ""
			, ""
			, true);

		if( ds_list8.GetColumn(0, "CNT") > 0 )
		{
			global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
			global.FRM_BOTTOM.st_msg.color = "yellow";       
			global.FRM_BOTTOM.st_msg.value = "같은 호기에 동일한 파일이 존재합니다.";
			return false;
		}



	return true;
}

/********************************************************************************
* 기      능   : Delete   --2015.07.01 문진원 GJ 추가요청
********************************************************************************/
function fn_delete() 
{	

	// 그리드 저장 대상 로우
	if(ds_list3.FindRow("CHECK", 1)==-1)
	{
		//gfn_showMsg('CW00046');
		//return;

		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "하위 리스트에서 정보를 먼저 선택해 주십시오.";
		return ;			
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("ps02:PS0201001B_U4");		                       
	tit_callService(
	  ""
	, ""
	, "ds_list3=ds_list3"
	, ""
	, ""
	, "fn_afterDelet" 
	, ""
	, ""
	, true);		




}

/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelet(errCode, errMsg) {
	if ( errCode!= 0 ) 
	{		
		gfn_showMsg("CE00001");		
	} 
	else 
	{
		gfn_showMsg("CI00004");
	}	

    fn_query();
}




