// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var userGroup = "";
var init = false;
var fv_end = false;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	init = true;

	grd_list.Format = "Default";
	grd_list.HeadHeight = 50;

	//대산엘리베이터,윤송이엔씨 외주업체는 'E40'권한으로 조회하도록 예외처리(20090304:한상협CJ)
	if( G_VEND_CODE_E == '1244298362' || G_VEND_CODE_E == '2098134940' || G_USER_ID == '2019736') //생산지원부 양철민GJ추가 
	{
		userGroup = '40';
	}

	if( userGroup == '10' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S11");
		tit_addSearchActionInfo("ps02:PS0201003A_S11");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzlifnr_cd=ds_zzlifnr_cd ds_zztemno_cd=ds_zztemno_cd"
			, ""
			, "fn_afterZzlifnrSearch"
			, true,true);
	}
	else if( userGroup == '30' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S31");
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_addSearchActionInfo("ps02:PS0201003A_S31");
		tit_addSearchActionInfo("ps02:PS0201003A_S32");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd ds_zzlifnr_cd=ds_zzlifnr_cd ds_zztemno_cd=ds_zztemno_cd"
			, ""
			, "fn_afterQuery30"
			, true,true);
	}
	else if( userGroup == '40' )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201003A_S41");
		tit_addSearchActionInfo("ps02:PS0201003A_S42");
		tit_addSearchActionInfo("ps02:PS0201003A_S43");
		tit_addSearchActionInfo("ps02:PS0201003A_S44");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd ds_zzlifnr_cd=ds_zzlifnr_cd ds_zztemno_cd=ds_zztemno_cd"
			, ""
			, "fn_afterQuery40"
			, true,true);
	}

	//권한에 따라 조회조건 필드 비활성화
	fn_authCheck();

	//PM지정일 : 현재월의 첫날짜, 현재날짜
	var firstDay = substr(gfn_getCurrDate(),0,6)+"01";
	var lastDay = gfn_getCurrDate();
	cal_datef.value = firstDay;
	cal_datet.value = lastDay;

	chk_zzgubun10.Value = "10";
// 	서울/경기 사무소 TEAM 코드화
	//========================서울/경기 사무소일경우 강남/강북팀 구분========================
	//chk_so3.Enable = true;
	//chk_so4.Enable = true;
	//chk_so3.Value = '1';
	//chk_so4.Value = '1';
	//=======================================================================================

	cb_gubun.SetFocus();
	cb_block.Value = '1';
	cb_gubun.Value = '1';
	cb_srcgbn.Index = 0;
	chk_s.Value = '1';
	chk_y.Value = '1';
	gfn_showMsg("CI00007");
}
//=========================================================================================
// 권한별 콤보박스 비활성화
//=========================================================================================
function fn_authCheck()
{
	if( userGroup == '10' )
	{
		cb_zzactss.Enable = false;
		cb_zzpmnum.Enable = false;
		cb_zzlifnr.Enable = false;
	}
	else if( userGroup == '30' )
	{
		cb_zzactss.Enable = false;
		cb_zzpmnum.Enable = true;
	}	
}
//=========================================================================================
// component Event 정의 : 각 Component 별 이벤트 
// 사무소값 변경되면 설치PM값을 filtering해준다
//=========================================================================================
function cb_zzactss_OnChanged(obj, strCode, strText, nOldIndex, nNewIndex)
{

	 if(userGroup == "40" || userGroup == "30"){
	ds_cond.SetColumn(0,"ZZLIFNR",'');  }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_zzlifnr_cd.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_zzlifnr_cd.Filter("ZZACTSS ==" +quote(arr));
    }


	cb_zzpmnum.value = "";
	if ( strCode == "") {
		ds_zzpmnum_cd.filter("ZZACTSS ==''");
	} else {
		ds_zzpmnum_cd.filter("ZZACTSS == " + quote( strCode) );
	}	
}
//=========================================================================================
// component Event 정의 : 각 Component 별 이벤트 
// 설치PM값 변경되면 협력업체값을 filtering해준다
//=========================================================================================
function cb_zzpmnum_OnChanged(obj, strCode, strText, nOldIndex, nNewIndex)
{
//return;
	cb_zzlifnr.value = "";
	cb_zztemno.value = "";
	if ( strCode == "") {
		ds_zzlifnr_cd.filter("ZZPMNUM ==''");
	} else {
		ds_zzlifnr_cd.filter("ZZPMNUM == " + quote( strCode) );
	}	
}
//=========================================================================================
// component Event 정의 : 각 Component 별 이벤트 
// 협력업체값 변경되면 작업팀장값을 filtering해준다
//=========================================================================================
function cb_zzlifnr_OnChanged(obj, strCode, strText, nOldIndex, nNewIndex)
{
	cb_zztemno.value = "";

	if ( strCode == "") {
		ds_zztemno_cd.filter("ZZLIFNR ==''");
	} else {
		ds_zztemno_cd.filter("ZZLIFNR == " + quote( strCode) );
	}	
}

