/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {

	if(init == true || fv_end == true)
	{

	}
	else
	{
		gfn_alert("잠시만 기다려 주십시오.");
		return;
	}

	fv_end = false;

	//구분필드 필수체크	
	if(gfn_isNull(cb_gubun, true, st_gubun.value))
		return;
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) )
		return;
	// 필수 입력사항 체크 BLOCK
	if(gfn_isNull(cb_block, true, st_block.value)) {
		return;
	}

// 	서울/경기 사무소 TEAM 코드화
	//========================서울/경기 사무소일경우 강남/강북팀 구분========================
	//if(cb_zzactss.value == '02' && chk_so3.Value == '0' && chk_so4.Value == '0') 
    //{
	//	gfn_showAlert("CW00022","서울/경기 사무소 선택시 서울1/2팀중 하나는 선택하셔야 합니다.");
	//	return;
	//}
	//=======================================================================================

	if(chk_zzgubun10.Value != '10' && chk_zzgubun11.Value != '11' && chk_zzgubun12.Value != '12' && chk_zzgubun20.Value != '20')
	{
		gfn_showMsg("CW00180",st_zzgubun.Value);
		return;
	}

	if(length(ds_cond.GetColumn(0,"ZZLIFNR")) <> 0 && chk_s.Value = '0' && chk_y.Value = '0') 
	{
	    gfn_showAlert("CW00022","업체 기준 조회 시 설치/양중 선택 시 하나는 선택하셔야 합니다.");
		return;
	}

	if(chk_zzgubun10.Value == '10') ds_cond.SetColumn(0,"ZZGUBUN10","10");
	else ds_cond.SetColumn(0,"ZZGUBUN10","");
	if(chk_zzgubun11.Value == '11') ds_cond.SetColumn(0,"ZZGUBUN11","11");
	else ds_cond.SetColumn(0,"ZZGUBUN11","");
	if(chk_zzgubun12.Value == '12') ds_cond.SetColumn(0,"ZZGUBUN12","12");
	else ds_cond.SetColumn(0,"ZZGUBUN12","");
	if(chk_zzgubun20.Value == '20') ds_cond.SetColumn(0,"ZZGUBUN20","20");
	else ds_cond.SetColumn(0,"ZZGUBUN20","");


	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	// 조회 처리 
	tit_callService(
        "PS0201003A_ACT"
        , "query"
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , false);
}

