// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

// 	서울/경기 사무소 TEAM 코드화
	//========================서울/경기 사무소일경우 강남/강북팀 구분========================
/*    for(var i=0; i<ds_list.rowcount; i++)
    {
		if(length(ds_list.GetColumn(i,"ZTEAM")) == 0)
		{
			ds_list.SetColumn(i,"ZTEAM","");
		}
	} 

	if(cb_zzactss.value == '02')
	{
		if(chk_so3.value == '1' && chk_so4.Value == '1') //전체팀
		{			

		}
		else if (chk_so3.Value == '1' && chk_so4.Value == '0') //1팀
		{						
			ds_list.Filter("ZTEAM==''");
		}
		else if (chk_so3.Value == '0' && chk_so4.Value == '1') //2팀
		{						
			ds_list.Filter("ZTEAM=='X'");
		}
	}
*/	

/* =============================  설치/양중 업체 필터============================*/
  var arr = ds_cond.GetColumn(0,"ZZLIFNR");

 if(length(ds_cond.GetColumn(0,"ZZLIFNR")) <> 0) 
 { //alert("a");
	if(chk_s.Value = '1' && chk_y.Value = '0')   // 설치업체 
	{   //alert("b");
	  ds_list.Filter("ZZLIFNR==" +quote(arr));
	}else if(chk_s.Value = '0' && chk_y.Value = '1'){   //alert("c"); //양중업체 
	  ds_list.Filter("ZZLIFNR2==" +quote(arr));
	}else{                                     // alert("d");        //전체
	  ds_list.Filter("ZZLIFNR==" +quote(arr) + " || ZZLIFNR2=="+quote(arr));
	}
 }

/* ===========================================================================*/	 


	ds_list_tmp.ClearData();
	ds_list_tmp.copyF(ds_list);

	ds_list.ClearData();
	ds_list.copyF(ds_list_tmp);
	ds_list_tmp.ClearData();
	//=======================================================================================

	if(ds_cond.GetColumn(0,"SRCGBN") == 1)
	{
		grd_list.Format = "Default";
		grd_list.HeadHeight = 50;

		for(var i=0; i<ds_list.rowcount; i++)
		{
			if(ds_list.GetColumn(i,"ZZSHIP1") != ds_list.GetColumn(i,"ZZPRO01") ||
			   ds_list.GetColumn(i,"ZZSHIP2") != ds_list.GetColumn(i,"ZZPRO02") ||
			   ds_list.GetColumn(i,"ZZSHIP3") != ds_list.GetColumn(i,"ZZPRO03") ||
			   ds_list.GetColumn(i,"ZZSHIP4") != ds_list.GetColumn(i,"ZZPRO04") ||
			   ds_list.GetColumn(i,"ZZSHIP5") != ds_list.GetColumn(i,"ZZPRO05") ||
			   ds_list.GetColumn(i,"ZZSHIP6") != ds_list.GetColumn(i,"ZZPRO06") )
			   ds_list.SetColumn(i,"R_GBN","R");
		}
	}
	else if(ds_cond.GetColumn(0,"SRCGBN") == 2)
	{
		grd_list.Format = "Default0";
		grd_list.HeadHeight = 30;
	}
	else if(ds_cond.GetColumn(0,"SRCGBN") == 3)
	{
		grd_list.Format = "Default1";
		grd_list.HeadHeight = 30;
	}
	else if(ds_cond.GetColumn(0,"SRCGBN") == 4)
	{
		grd_list.Format = "Default2";
		grd_list.HeadHeight = 30;
	}

	chk_chggbn_OnClick("", chk_chggbn.Value);
	chk_excpcancel_OnClick("", chk_excpcancel.Value);




	// 메시지 처리 
	gfn_showSearchMsg(grd_list, errCode, errMsg);

	ds_list.row = -1;

	fv_end = true;
	init = false;
}
/********************************************************************************
* 기      능   : 조회 Callback  - 설치협력사 로그인시 협력업체코드 첫번째 선택
********************************************************************************/
function fn_afterZzlifnrSearch(errCode, errMsg) {
	cb_zzlifnr.Index = 0;
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery30(errCode, errMsg) {
	cb_zzactss.Index = 0;
	//코드콤보 설정 : 협력사 전체표시
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
	gfn_insertEmptyRow( ds_zzlifnr_cd, G_CODE_ALL );
	cb_zzlifnr.value = "";
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery40(errCode, errMsg) {
	//코드콤보 설정 : 사무소 전체표시
	gfn_insertEmptyRow( ds_zzactss_cd, G_CODE_ALL );
	cb_zzactss.value = "";
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
	//코드콤보 설정 : 협력사 전체표시
	gfn_insertEmptyRow( ds_zzlifnr_cd, G_CODE_ALL );
	cb_zzlifnr.value = "";
}
