/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 현재 처리할 format 기억
		gfn_selectGridCol("grd_list");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능  관 :  출하요청일 과 생관출하조정일이 다른경우 테스트 빨강색으로 표시
********************************************************************************/
function fn_changeColor(currow,zzship,zzpro)
{
	if(ds_list.getcolumn(currow,zzship) != ds_list.getcolumn(currow,zzpro))
	{
		return "red";
	}	
	else
		return "default";
}

function chk_chggbn_OnClick(obj,strValue)
{
	if(ds_cond.GetColumn(0,"SRCGBN") != 1) return;

	ufn_setFilterForList();
	ds_list.row = -1;

	gfn_showMsg("CI00002", ds_list.rowCount() + "");
}

function chk_excpcancel_OnClick(obj, strValue) {
	ufn_setFilterForList();
	ds_list.row = -1;	

	gfn_showMsg("CI00002", ds_list.rowCount() + "");
}
function ufn_setFilterForList() {
	var strFilter = "";
	if(chk_chggbn.Value == true)		strFilter  = "R_GBN == 'R'&&";
	if(chk_excpcancel.Value == true)	strFilter += "TXT04 != 'C'&&";

	if(Right(strFilter,2) == "&&") 		strFilter = Left(strFilter,Length(strFilter)-2);
	ds_list.filter(strFilter);
}

function cb_gubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == '1') {
		cb_block.Enable = true;
	} else {
		cb_block.Enable = false;
	}
}
