var userGroup = "";
var fv_init   = "N";
var fv_wait   = true;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();


	if(G_USER_ID == '2028433' || G_USER_ID == '3018598' || G_USER_ID == '2019736' || G_USER_ID == '2029847' ||
	   G_USER_ID == 'HF300A06' || G_USER_ID == '2023806' || G_USER_ID == '2014793' || G_USER_ID == '2028352' || G_USER_ID == '2015960') // 박남선, 신경철, 황명순, 정민제 
	{
		cal_date.Enable = true;
		Static1.Visible = true;
		cb_team.Visible = true;
	}
	else
	{
		cal_date.Enable = false;
		Static1.Visible = false;
		cb_team.Visible = false;

		Static7.Left = 5;
		cb_zzactss.Left = 87;
		Static2.Left = 189;
		cb_zzlifnr.Left = 271;
		st_zztemno.Left = 373;
		cb_zztemno.Left = 455;

	}

	//PM지정일 : 현재월의 첫날짜, 현재날짜
	var initDay = gfn_getCurrDate();
	//fv_init = 'Y';	// 초기상태
	fv_wait = true;
	ds_cond.fireevent = false;
	ds_cond.SetColumn(0, "STND_DATE", initDay);
	ds_cond.fireevent = true;

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201009A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_standDt=ds_standDt"
		, ""
		, "fn_afterDay"
		, true);

	cb_gubun.SetFocus();
	cb_block.Value = 'A';
	rd_ra.Value = 'A';
	fn_authCheck();
}

function fn_afterDay()
{
	ds_cond.SetColumn(0, "STND_DATE", ds_standDt.getColumn(0, "STND_DATE"));
	//ds_cond.SetColumn(0, "STND_WEEK", ds_standDt.getColumn(0, "STND_WEEK"));

	if( userGroup == '10' )
	{
		ds_gubun_cd.Filter("CODE != 'W0'");
		cal_date.Enable = false;
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S11");
		tit_addSearchActionInfo("ps02:PS0201003A_S11");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzlifnr_cd=ds_zzlifnr_cd ds_zztemno_cd=ds_zztemno_cd"
			, ""
			, "fn_afterZzlifnrSearch"
			, true,true);
	}
	else if( userGroup == '30' )
	{
		ds_gubun_cd.Filter("CODE != 'W0'");
		cal_date.Enable = false;
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S31");
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_addSearchActionInfo("ps02:PS0201003A_S31");
		tit_addSearchActionInfo("ps02:PS0201003A_S32");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd ds_zzlifnr_cd=ds_zzlifnr_cd ds_zztemno_cd=ds_zztemno_cd"
			, ""
			, "fn_afterQuery30"
			, true,true);
	}
	else if( userGroup == '40' )
	{
		//cal_date.Enable = true;
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201003A_S41");
		tit_addSearchActionInfo("ps02:PS0201003A_S42");
		tit_addSearchActionInfo("ps02:PS0201003A_S43");
		tit_addSearchActionInfo("ps02:PS0201003A_S44");
		tit_addSearchActionInfo("ps02:PS0201009A_S5");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd ds_zzlifnr_cd=ds_zzlifnr_cd ds_zztemno_cd=ds_zztemno_cd ds_team=ds_team"
			, ""
			, "fn_afterQuery40"
			, true,true);
	}
}

function fn_afterWeek()
{
	ds_cond.setColumn(0, "W2", ds_week.getColumn(0, "WEEK_W2"));
	ds_cond.setColumn(0, "W3", ds_week.getColumn(0, "WEEK_W3"));
	ds_cond.setColumn(0, "W4", ds_week.getColumn(0, "WEEK_W4"));
	ds_cond.setColumn(0, "W6", ds_week.getColumn(0, "WEEK_W6"));

	ds_cond.setColumn(0, "GJAHR_W2", ds_week.getColumn(0, "GJAHR_W2"));
	ds_cond.setColumn(0, "GJAHR_W3", ds_week.getColumn(0, "GJAHR_W3"));
	ds_cond.setColumn(0, "GJAHR_W4", ds_week.getColumn(0, "GJAHR_W4"));
	ds_cond.setColumn(0, "GJAHR_W6", ds_week.getColumn(0, "GJAHR_W6"));

	ds_cond.SetColumn(0, "STND_WEEK", ds_week.GetColumn(0, "STND_WEEK"));

	ds_cond.SetColumn(0, "WEEK_W2_ST_DT", ds_week.GetColumn(0, "WEEK_W2_ST_DT"));
	ds_cond.SetColumn(0, "WEEK_W2_FI_DT", ds_week.GetColumn(0, "WEEK_W2_FI_DT"));
	ds_cond.SetColumn(0, "WEEK_W3_ST_DT", ds_week.GetColumn(0, "WEEK_W3_ST_DT"));
	ds_cond.SetColumn(0, "WEEK_W3_FI_DT", ds_week.GetColumn(0, "WEEK_W3_FI_DT"));
	ds_cond.SetColumn(0, "WEEK_W4_ST_DT", ds_week.GetColumn(0, "WEEK_W4_ST_DT"));
	ds_cond.SetColumn(0, "WEEK_W4_FI_DT", ds_week.GetColumn(0, "WEEK_W4_FI_DT"));
	ds_cond.SetColumn(0, "WEEK_W6_ST_DT", ds_week.GetColumn(0, "WEEK_W6_ST_DT"));
	ds_cond.SetColumn(0, "WEEK_W6_FI_DT", ds_week.GetColumn(0, "WEEK_W6_FI_DT"));

	//Trace(ds_cond.saveXML());
	fv_wait = false;

	/*
	if(fv_init == 'N')
	{
		ds_cond.SetColumn(0, "STND_WEEK", ds_week.GetColumn(0, "STND_WEEK"));
	}
	*/
	//fn_query();
}

//=========================================================================================
// 권한별 콤보박스 비활성화
//=========================================================================================
function fn_authCheck()
{
	if( userGroup == '10' )
	{
		cb_zzactss.Enable = false;
		cb_zzpmnum.Enable = false;
		cb_zzlifnr.Enable = false;
	}
	else if( userGroup == '30' )
	{
		cb_zzactss.Enable = false;
		cb_zzpmnum.Enable = true;
	}	
}
//=========================================================================================
// component Event 정의 : 각 Component 별 이벤트 
// 사무소값 변경되면 설치PM값을 filtering해준다
//=========================================================================================
function cb_zzactss_OnChanged(obj, strCode, strText, nOldIndex, nNewIndex)
{

	if(userGroup == "40" || userGroup == "30"){
		ds_cond.SetColumn(0,"ZZLIFNR",'');  }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_zzlifnr_cd.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_zzlifnr_cd.Filter("ZZACTSS ==" +quote(arr)+"|| CODE==''");
    }


	cb_zzpmnum.value = "";
	if ( strCode == "") {
		ds_zzpmnum_cd.filter("ZZACTSS ==''");
	} else {
		ds_zzpmnum_cd.filter("ZZACTSS == " + quote( strCode) +"|| CODE==''");
	}	
}
//=========================================================================================
// component Event 정의 : 각 Component 별 이벤트 
// 설치PM값 변경되면 협력업체값을 filtering해준다
//=========================================================================================
function cb_zzpmnum_OnChanged(obj, strCode, strText, nOldIndex, nNewIndex)
{
return;
	cb_zzlifnr.value = "";
	cb_zztemno.value = "";
	if ( strCode == "") {
		ds_zzlifnr_cd.filter("ZZPMNUM ==''");
	} else {
		ds_zzlifnr_cd.filter("ZZPMNUM == " + quote( strCode) +"|| CODE==''");
	}	
}
//=========================================================================================
// component Event 정의 : 각 Component 별 이벤트 
// 협력업체값 변경되면 작업팀장값을 filtering해준다
//=========================================================================================
function cb_zzlifnr_OnChanged(obj, strCode, strText, nOldIndex, nNewIndex)
{
	cb_zztemno.value = "";

	if ( strCode == "") {
		ds_zztemno_cd.filter("ZZLIFNR ==''");
	} else {
		ds_zztemno_cd.filter("ZZLIFNR == " + quote( strCode) +"|| CODE==''");
	}	
}

