/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {

	if(Length(Trim(ed_pspid.Text)) < 6)	// 프로젝트 번호가 없을 경우
	{
		if(Length(Trim(cal_date.Value)) < 8)	// 기준일은 필수
		{
			gfn_openMsgWin("기준일을 선택해 주세요.");
			return;
		}
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201009A_S3"); 
	tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery"
			, true);
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {

	// 변경된 ROW가 없을 시
	if(ds_list.GetOrgBuffCount() == 0)
	{
		gfn_openMsgWin("변경된 내역이 없습니다.");
		return;
	}

	//ds_list_tmp.ClearData();
	//ds_list.Filter("FLAG == 'U'");
	//ds_list_tmp.copyF(ds_list);
	//ds_list.unFilter();
	gfn_copyDs(ds_list, ds_list_tmp, "FLAG='U'");



	tit_clearActionInfo();

	tit_addMultiActionInfo("ps02:PS0201009A_I1");
	tit_callService(
			""
			, ""
			, "ds_list=ds_list_tmp"
			, ""
			, ""
			, "fn_afterSave"
			, true);

}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");	
		fn_query();	
	}

}

