/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

	if(ds_list.GetRowCount() > 0)
	{
		tit_callService(
        "PS0201009A_ACT"
        , "query"
        , "ds_cond=ds_cond ds_list=ds_list"
        , "ds_list_rfc=ds_list_tmp"
        , ""
        , "fn_afterQuery2"
        , false); 
	}

	fv_init = 'N';	// 초기상태가 아닌 것으로 변경.

	// 메시지 처리 
	gfn_showSearchMsg(grd_list, errCode, errMsg);

	ds_list.row = -1;
}

/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery2() {
	var nFindRow = 0;

	for(var i=0;i<ds_list.getRowCount();i++)
	{
		nFindRow = ds_list_rfc.SearchRow("POSID == '"+ds_list.getColumn(i, "POSID")+"'");
		if(nFindRow >= 0)
		{
			ds_list.SetColumn(i, "SPEC", ds_list_rfc.GetColumn(nFindRow, "SPEC"));
			ds_list.SetColumn(i, "LAYOUT", ds_list_rfc.GetColumn(nFindRow, "LAYOUT"));
			ds_list.SetColumn(i, "MONEY", ds_list_rfc.GetColumn(nFindRow, "MONEY"));
		}
	}
}
