* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery30(errCode, errMsg) {
	//Trace(ds_zzactss_cd.saveXML());
	cb_zzactss.Index = 0;
	//코드콤보 설정 : 협력사 전체표시
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
	gfn_insertEmptyRow( ds_zzlifnr_cd, G_CODE_ALL );
	cb_zzlifnr.value = "";

	gfn_insertEmptyRow( ds_zztemno_cd, G_CODE_ALL );
	cb_zztemno.value = "";

	cb_gubun.Index = 0;
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery40(errCode, errMsg) {

	gfn_insertEmptyRow( ds_team, G_CODE_ALL );
	cb_team.value = "";

	//코드콤보 설정 : 사무소 전체표시
	gfn_insertEmptyRow( ds_zzactss_cd, G_CODE_ALL );
	cb_zzactss.value = "";
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
	//코드콤보 설정 : 협력사 전체표시
	gfn_insertEmptyRow( ds_zzlifnr_cd, G_CODE_ALL );
	cb_zzlifnr.value = "";

	gfn_insertEmptyRow( ds_zztemno_cd, G_CODE_ALL );
	cb_zztemno.value = "";

	cb_gubun.Index = 0;


}
