/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 1 ) {
		// 현재 처리할 format 기억
		gfn_selectAllGrid(obj,nCell,nCell-1,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}


function ds_cond_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "STND_DATE")
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201009A_S2");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_week=ds_week"
			, ""
			, "fn_afterWeek"
			, true);
	}
}



function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == 'PM_CNFM' || strColumnID == 'BIGO')
	{
		var sOldValue1 = ds_list.GetOrgColumn(nRow, "PM_CNFM");
		var sNewValue1 = ds_list.GetColumn(nRow, "PM_CNFM");
		var sOldValue2 = ds_list.GetOrgColumn(nRow, "BIGO");
		var sNewValue2 = ds_list.GetColumn(nRow, "BIGO");

		if((sOldValue1 != sNewValue1) || (sOldValue2 != sNewValue2))
		{
			ds_list.SetColumn(nRow, "FLAG", "U");
		}
		else
		{
			ds_list.SetColumn(nRow, "FLAG", "");
		}
	}
}


