function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == '16') // 출하변경 버튼
	{
		var formID="";
		var gbn = "CHULHA";
		var pjt = ds_list.GetColumn(nRow, "PSPID");
		var hno = ds_list.GetColumn(nRow, "POSID");
		var bill = ds_list.GetColumn(nRow, "COLLECT_BILL");
		var gbn  = ds_list.GetColumn(nRow, "GBN");

		formID = "PS0204008C";  // 

		var arg = "s_pspid="+quote(pjt)+
				  " s_hno="+quote(hno)+
				  " s_bill="+quote(bill)+
				  " F_MENU="+quote(gbn)+
				  " s_gbn="+quote(gbn);

		gfn_openPopup("PS0204008C", true, arg);  
	}
	else if(nCell == '17')	// 현장관리(답사) 버튼
	{
		var formID="";
		var gbn = "SELECT";
		var pjt = ds_list.GetColumn(nRow, "PSPID");

		formID = "PS0201001A";  // 착/준공 계획대상 조회

		var arg = "s_pspid="+quote(pjt)+
				  " s_gbn="+quote(gbn)+
				  " s_inter=''" ;

		gfn_openWin("PS0201001A",arg);  
	}
}

//설치팀 변경 시 -> 사무소 필터
function cb_team_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_cond.SetColumn(0,"ZZACTSS",'');
	ds_cond.SetColumn(0,"ZZPMNUM",''); 
	ds_cond.SetColumn(0,"ZZLIFNR",'');
	ds_cond.SetColumn(0,"ZZTEMNO",'');

	ds_zzactss_cd.UnFilter();
	ds_zzpmnum_cd.UnFilter();
	ds_zzlifnr_cd.UnFilter();
	ds_zztemno_cd.UnFilter();

    if(ds_cond.GetColumn(0,"TEAM") == '')
    {
		//ds_zzactss_cd.UnFilter();
    }
    else
    {
		ds_zzactss_cd.Filter("HIGH_CODE ==" +quote(strCode)+"|| CODE==''");
    }


}
