#include "LIB::commons.js";	// 공통 스크립트 
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var userGroup = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();


	//PM지정일 : 현재월의 첫날짜, 현재날짜
	var initDay = gfn_getCurrDate();

	ds_cond.fireevent = false;
	ds_cond.SetColumn(0, "DATEF", initDay);
	ds_cond.SetColumn(0, "DATET", gfn_getAfterDate(initDay,365));
	ds_cond.fireevent = true;

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0710009A_S2");   // 영업지사
	tit_addSearchActionInfo("ps07:PS0710009A_S3");	// 영업담당자
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_combo2=ds_combo2 ds_vkgrp=ds_vkgrp"
		, ""
		, "fn_afterDay"
		, true);


	fn_authCheck();
}

function fn_afterDay()
{

	gfn_insertEmptyRow( ds_vkgrp, G_CODE_ALL );
	gfn_insertEmptyRow( ds_sman, G_CODE_ALL );
	cb_vkgrp.value = "";	
	cb_sman.value = "";	
	cb_vkgrp.Index = 0;
	cb_sman.Index = 0;


	//Trace(SubStr(G_USER_ID,0,1));
	if(SubStr(G_USER_ID,0,1) == '9')
	{

	}
	else
	{
		div_sman.ed_sman_cd.Value = "";
		div_sman.ed_sman_nm.Value = "";
	}

	if( userGroup == '30' )
	{
		ds_gubun_cd.Filter("CODE != 'W0'");
		cal_date.Enable = false;
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201001A_S31");
		tit_addSearchActionInfo("ps02:PS0201001A_S32");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd"
			, ""
			, "fn_afterQuery30"
			, true,true);
	}
	else
	{
		//cal_date.Enable = true;
		tit_clearActionInfo();
		tit_addSearchActionInfo("ps02:PS0201003A_S41");
		tit_addSearchActionInfo("ps02:PS0201003A_S42");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_zzactss_cd=ds_zzactss_cd ds_zzpmnum_cd=ds_zzpmnum_cd"
			, ""
			, "fn_afterQuery40"
			, true,true);
	}

}


//=========================================================================================
// 권한별 콤보박스 비활성화
//=========================================================================================
function fn_authCheck()
{
	if( userGroup == '30' )
	{
		cb_zzactss.Enable = false;
		cb_zzpmnum.Enable = true;
	}	
}
//=========================================================================================
// component Event 정의 : 각 Component 별 이벤트 
// 사무소값 변경되면 설치PM값을 filtering해준다
//=========================================================================================
function cb_zzactss_OnChanged(obj, strCode, strText, nOldIndex, nNewIndex)
{
	ds_cond.SetColumn(0,"ZZLIFNR",'');

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_zzlifnr_cd.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_zzlifnr_cd.Filter("ZZACTSS ==" +quote(arr)+"|| CODE==''");
    }


	cb_zzpmnum.value = "";
	if ( strCode == "") {
		ds_zzpmnum_cd.filter("ZZACTSS ==''");
	} else {
		ds_zzpmnum_cd.filter("ZZACTSS == " + quote( strCode) +"|| CODE==''");
	}	
}


