/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {

	if(Length(Trim(ed_pspid.Text)) < 6)	// 프로젝트 번호가 없을 경우
	{
		if(Length(Trim(cal_datef.Value)) < 8 || Length(Trim(cal_datet.Value)) < 8)	// 기준일은 필수
		{
			gfn_openMsgWin("예정일을 선택해 주세요.");
			return;
		}
	}

	ds_cond.SetColumn(0, "SMAN",   div_sman.ed_sman_cd.Value);

	ds_cond_temp.ClearData();
	ds_cond_temp.AddRow();
	ds_cond_temp.SetColumn(0, "I_DATEF", ds_cond.GetColumn(0, "DATEF"));
	ds_cond_temp.SetColumn(0, "I_DATET", ds_cond.GetColumn(0, "DATET"));
	ds_cond_temp.SetColumn(0, "I_POSID", ds_cond.GetColumn(0, "POSID"));
	ds_cond_temp.SetColumn(0, "I_ZZACTSS", ds_cond.GetColumn(0, "ZZACTSS"));
	ds_cond_temp.SetColumn(0, "I_ZZPMNUM", ds_cond.GetColumn(0, "ZZPMNUM"));
	ds_cond_temp.SetColumn(0, "I_VKGRP", ds_cond.GetColumn(0, "VKGRP"));
	ds_cond_temp.SetColumn(0, "I_SMAN", ds_cond.GetColumn(0, "SMAN"));



	tit_callService(
        "PS0201011A_ACT"
        , "query2"
        , "ds_cond=ds_cond_temp"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , false); 
}


