var userGroup = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	if(userGroup == '10')
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	if( userGroup != '40' )
	{
		cb_type1.Enable = false;
		cb_type2.Enable = false;
		cal_datef.Enable = false;
		cal_datet.Enable = false;
		ufn_enableBtn("query", false);
		Button4.Enable = false;

		gfn_showMsg("ZW00003");
		return;
	}

	cb_type1.Index = 0;
	cb_type2.Index = 0;

	ufn_enableBtn("query", true);
	Button4.Enable = true;

	//PM지정일 : 현재월의 첫날짜, 현재날짜
	var firstDay = substr(gfn_getCurrDate(),0,6)+"01";
	var lastDay = gfn_getCurrDate();
	cal_datef.value = firstDay;
	cal_datet.value = lastDay;

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

// 	서울/경기 사무소 TEAM 코드화
	//========================서울/경기 사무소일경우 강남/강북팀 구분========================
//	chk_soa.Enable = true;
//	chk_sob.Enable = true;
//	chk_soa.Value = '1';
//	chk_sob.Value = '1';
	//=======================================================================================
	ds_cond.SetColumn(0, "CLSSFY", 'A');
	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps02:PS0203001A_S1");
	tit_addSearchActionInfo("ps02:PS0201001A_S34");

	tit_callService(
	  ""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterZzlifnr"
		, true,true);


	gfn_showMsg("CI00007");
}

