/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
		// 현재 처리할 format 기억
		//gfn_selectGridCol("grd_list");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 마감
********************************************************************************/
function Button4_OnClick(obj)
{
	if ( !gfn_isChangeDs(ds_list, true, "")){
		return;
	}
	// 마감 처리 
	tit_callService(
        "PS0203001A_ACT"
        , "save"
        , "ds_list=ds_list:A ds_cond=ds_cond_bak"
        , "ds_error=ds_error ds_list=ds_list"
        , "" 
        , "fn_afterSave");

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( gfn_showWSSaveMsgForQM("grd_list", ds_error, "CHK", errCode, errMsg) ) {
		// error
		return;
	}
}
/********************************************************************************
* 기      능   : 사무소 변경시 협력업체 FILTERING
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == '') ds_lifnr.Filter("SO5=='' || CODE==''");
	else ds_lifnr.Filter("(ZZACTSS=="+quote(strCode)+" && SO5=='') || CODE=='' ");
	cb_zzlifnr.Index = 0;
}

function btn_popProj_OnClick(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A", true, arg);
}

/********************************************************************************
* 기      능   : 사업부 선택시 ds_lifnr 데이터셋 값 filtering
********************************************************************************/
function chk_so2_OnClick(obj,strValue)
{
	var so = ' ';
	var so2 = ' ';
	var so3 = ' ';
	var so4 = ' ';
	var so5 = ' ';
	var so7 = ' ';

	if(chk_so.Value == '1')  so =  'X';
	if(chk_so2.Value == '1') so2 = 'X';
	if(chk_so3.Value == '1') so3 = 'X';
	if(chk_so4.Value == '1') so4 = 'X';
	if(chk_so5.Value == '1') so5 = 'X';
	if(chk_so7.Value == '1') so7 = 'X';


	if(chk_so.Value+chk_so2.Value+chk_so3.Value+chk_so4.Value+chk_so5.Value+chk_so7.Value == '000000')
	{
		ds_lifnr.Filter("SO5=='' || CODE==''");
	}	
	else
	{
	  ds_lifnr.Filter("CODE=='' || SO=="+quote(so) +" || SO2=="+quote(so2) +" || SO3=="+quote(so3) +" || SO4=="+quote(so4) +" || SO5=="+quote(so5) +" || SO7=="+quote(so7));
	}

	cb_zzlifnr.Index = 0;	
}

function chk_excpcancel_OnClick(obj,strValue)
{
	ufn_setFilterForList();
	ds_list.row = -1;	

	gfn_showMsg("CI00002", ds_list.rowCount() + "");	
}


function ufn_setFilterForList() {
	var strFilter = "";	

	if(chk_excpcancel.Value == true)	strFilter += "TXT04 != 'C'&&";

	if(Right(strFilter,2) == "&&") 		strFilter = Left(strFilter,Length(strFilter)-2);
	ds_list.filter(strFilter);
}
