var userGroup = "";
var str = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);	

	userGroup = gfn_getUserGroup();

	if(userGroup == '10')
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	var codeArr = ["PS003"];
    var dsObjArr = [ds_zzactss];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	ds_cond.SetColumn(0, "CLSSFY", 'A');

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps04:PS0401002A_S1");
    tit_addSearchActionInfo("ps02:PS0201001A_S34");
	tit_callService(
	  ""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterQueryC"
		, true,true);

	ed_pjt.SetFocus();
}
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	var gbn10 = chk_zzgubun10.Value;
	var gbn11 = chk_zzgubun11.Value;
	var gbn12 = chk_zzgubun12.Value;
	var gbn20 = chk_zzgubun20.Value;

	var arg = "";
	if(gbn10 == "1")
		arg += "'10'"+",";
	if(gbn11== "1")
		arg += "'11'"+",";
	if(gbn12 == "1")
		arg += "'12'"+",";
	if(gbn20 == "1")
		arg += "'20'";

	if(substr(arg,length(arg)-1) == ",")
		arg = substr(arg,0,length(arg)-1);

	arg = "GUBUN = "+ quote(arg);
	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps02:PS0204002A_S1");  //==> CsvSaerch로 변경
	tit_addCsvSearchActionInfo("ps02:PS0204002A_S1"); 

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , arg
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {

	//취소제외
	chk_excpcancel_OnClick("", chk_excpcancel.Value);


	if( userGroup == "10")
	{
		ds_lifnr.filter("CODE == " + quote( G_VEND_CODE_E) );
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;
	}
	else// if ( userGroup == "40" )
	{
		cb_lifnr.Enable = true;
		ds_lifnr.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		cb_lifnr.value = "";
	}	
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
