var userGroup = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();

	//가원산업개발, 대륙산업개발 일 경우 40번 권한으로
	if( G_VEND_CODE_E == '6212132828' || G_VEND_CODE_E == '2062376067' )
	{
		userGroup = '40';
	}


	if( userGroup == '10' )
	{
		chk_so.Enable = false;
		chk_so2.Enable = false;
		chk_so3.Enable = false;
		chk_so4.Enable = false;
		chk_so5.Enable = false;
		chk_so7.Enable = false;
	}
	else
	{
		chk_so.Enable = true;
		chk_so2.Enable = true;
		chk_so3.Enable = true;
		chk_so4.Enable = true;
		chk_so5.Enable = true;
		chk_so7.Enable = true;
	}

	var codeArr = ["PS003"];
	var dsObjArr = [ds_zzactss];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
// 	서울/경기 사무소 TEAM 코드화
	//========================서울/경기 사무소일경우 강남/강북팀 구분========================
//	chk_soa.Enable = true;
//	chk_sob.Enable = true;
//	chk_soa.Value = '1';
//	chk_sob.Value = '1';
	//=======================================================================================

	ds_cond.SetColumn(0, "CLSSFY", 'A');


	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0204007A_S2");
	tit_addSearchActionInfo("ps02:PS0201001A_S34");
	//tit_addSearchActionInfo("ps02:PS0201001A_S33");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_pm=ds_pm ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterZzLifnr"
		, true,true);

	ed_pspid.SetFocus();
	gfn_showMsg("CI00007");

	cb_gbn.Index = 0;
	cb_gbn2.Index = 0;
	//rd_chk1.Value = 1;
	//rd_chk1.INDEX = 1;




}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_zzactss.Index = 0;
}
