/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	// 필수 입력사항 및 일자 체크
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) ) {
		return;
	}
	//설치협력사 조회시 협력업체 필수체크
	if(userGroup == '10' && gfn_isNull(cb_zzlifnr, true, st_zzlifnr.value)) {
		return;
	}
	//사무소 조회시 사무소 필수체크
	if(userGroup == '30' && gfn_isNull(cb_zzactss, true, st_zzactss.value)) {
		return;
	}
	if(cal_datef.Value == '' ){
		//프로젝트필드 필수체크
		if(gfn_isNull(ed_pspid, true, st_pspid.value))
			return;
	}
// 	서울/경기 사무소 TEAM 코드화
	//========================서울/경기 사무소일경우 강남/강북팀 구분========================
	//if(cb_zzactss.value == '02' && chk_soa.Value == '0' && chk_sob.Value == '0') 
    //{
	//	gfn_showAlert("CW00022","서울/경기 사무소 선택시 서울1/2팀중 하나는 선택하셔야 합니다.");
	//	return;
	//}
	//=======================================================================================


//PS0204007A_S3

	if (rd_chk1.index == 0)
	{

		tit_clearActionInfo();
		//tit_addSearchActionInfo("ps02:PS0204007A_S1");  //==> CsvSaerch로 변경
		tit_addCsvSearchActionInfo("ps02:PS0204007A_S1");  

		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);





    }
    else if(rd_chk1.index == 1)
    {
		//alert("1");
		tit_clearActionInfo();
		//tit_addSearchActionInfo("ps02:PS0204007A_S3");  //==> CsvSaerch로 변경
		tit_addCsvSearchActionInfo("ps02:PS0204007A_S3"); 


		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery"
			, ""
			, ""			
			, true);

    }


}
