/********************************************************************************
* 기      능   : 조회 Callback  - 설치협력사 로그인시 협력업체코드 첫번째 선택
********************************************************************************/
function fn_afterZzLifnr(errCode, errMsg) {
	gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
	gfn_insertEmptyRow( ds_pm, G_CODE_ALL );
	if(userGroup == '10')
	{
		ds_zzactss.Filter("1=2");
		cb_zzactss.value = "";
		cb_zzactss.enable = false;

		ds_pm.Filter("1=2");
		cb_pm.value = "";
		cb_pm.enable = false;

		ds_lifnr.Filter("CODE="+quote(G_VEND_CODE_E));
		cb_zzlifnr.index = 0;
		cb_zzlifnr.enable = false;
	}
	else if(userGroup == '30')
	{

//trace(ds_zzactss.SaveXML());
//alert(G_AREA_CODE_E);	
		ds_zzactss.Filter("CODE=="+quote(G_AREA_CODE_E));
		cb_zzactss.index = 0;
		cb_zzactss.enable = false;

		cb_pm.value = "";
		cb_pm.enable = true;

		cb_zzlifnr.value = "";
		cb_zzlifnr.enable = true;
	}
	else if(userGroup == '40')
	{
		cb_zzactss.value = "";
		cb_zzactss.enable = true;

		cb_pm.value = "";
		cb_pm.enable = true;

		cb_zzlifnr.value = "";
		cb_zzlifnr.enable = true;
	}
	else
	{
		ds_lifnr.Filter("1=2");
		cb_zzlifnr.value = "";
		cb_zzlifnr.enable = false;

		ds_zzactss.Filter("1=2");
		cb_zzactss.value = "";
		cb_zzactss.enable = false;

		ds_pm.Filter("1=2");
		cb_pm.value = "";
		cb_pm.enable = false;
	}
}
/********************************************************************************
* 기      능   : 사무소콤보 변경시 설치PM 콤보값 생성
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
  if(userGroup == "40" || userGroup == "30"){
	ds_cond.SetColumn(0,"ZZLIFNR",''); }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_lifnr.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_lifnr.Filter("ZZACTSS ==" +quote(arr));
    }


	if(strCode == '') ds_pm.Filter("1=1");
	else ds_pm.Filter("CODE='' OR ZZACTSS="+quote(strCode));
	cb_pm.Index = 0;
}
